main.css:
@keyframes bounce {
  0% {
    top: 50%;
  }

  50% {
    top: 40%;
  }

  100% {
    top: 50%;
  }
}

.cRe3Fvf.bp-main * {
    box-sizing: border-box;    
}

.cRe3Fvf.bp-main {
    width: 100%; 
    height: 100vh;
}

.cRe3Fvf.bp-main *:not([class^=.material-]) {
    font-family: Pretendard;
}

/*BODY : S*/
.cRe3Fvf .body-wrap {
    margin-bottom: 60px;
}

.cRe3Fvf .body-product {
    width: 20%;
}

.cRe3Fvf .body-product-image {
    min-height: 240px;
    background-repeat: no-repeat;
    background-size: 90%;
    background-position: center;
}

.cRe3Fvf .body-product-title {
    font-size: 20px;
    font-weight: 600;
    margin-top: 20px;
}

.cRe3Fvf .body-product-comment {
    font-size: 16px;
    color: #8b8b8b;
    margin-top: 5px;
}

.cRe3Fvf .body-product-tags {
    display: flex;
    margin-top: 20px;
    font-size: 14px;
}

.cRe3Fvf .body-product-tag {
    padding: 5px;
    color: #fff;
    border-radius: 5px;
}

.cRe3Fvf .body-product-tag:not(:first-child):not(:last-child) {
    margin: 0px 5px;
}

.cRe3Fvf .body-products {
    width: 100%;
    display: flex;
    gap: 10px;
    padding: 30px 0px;
    margin-bottom: 60px;
}


.cRe3Fvf .slider-wrap {
    height: 600px;
    background: -webkit-linear-gradient(to right, #CFDEF3, #E0EAFC);
    background: linear-gradient(to right, #CFDEF3, #E0EAFC);
    margin-bottom: 60px;
}

.cRe3Fvf .slider-box {
    height: 100%;
}
/*BODY : E*/



.cRe3Fvf .new-product {
    width: 20%;
}

.cRe3Fvf .new-product-image {
    min-height: 240px;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    border-radius: 10px;
}

.cRe3Fvf .new-product-title {
    font-size: 14px;
    margin-top: 10px;
    color: #1A1A1A;
    height: 35px;
    line-height: 1.2;
}

.cRe3Fvf .new-product-comment {
    font-size: 16px;
    color: #8b8b8b;
    margin-top: 5px;
}

.cRe3Fvf .new-products {
    background-color: #F6F7FA;
    padding: 30px;
    margin-bottom: 60px;
}

.cRe3Fvf .new-product-subject {
    font-size: 23px;
    font-weight: 600;
    color: #222;
    margin-bottom: 30px;
}

.cRe3Fvf .new-products-list {
    width: 100%;
    display: flex;
    gap: 20px;
}

.cRe3Fvf .new-product-tag {
    font-size: 14px;
    display: inline-block;
    padding: 5px 10px;
    margin-top: 10px;
    color: #fff;
    background: linear-gradient(90deg, rgb(221 2 2) 0%, rgb(231 9 9) 100%);
    border-radius: 5px;
}
.cRe3Fvf .new-product-bottom {
    display: flex;
    justify-content: space-between;
    margin-top: 20px;
    font-size: 18px;
    font-weight: 600;
}

.cRe3Fvf .new-product-per {
    color: #F14738;
}

.cRe3Fvf .recommend-product {
    display: flex;
    gap: 20px;
    margin-bottom: 60px;
}

.cRe3Fvf .recommend-product-category {
    width: 230px;
}
.cRe3Fvf .recommend-product-list {
    width: calc(100% - 230px);
    display: flex;
    flex-wrap: wrap;
    gap: 20px
}
.cRe3Fvf .recommend-product-item {
    width: calc(33.333% - 13.4px);
    position: relative;
    overflow: hidden;
    cursor:pointer;
    justify-content: space-between;
}

.cRe3Fvf .recommend-product-item:hover .recommend-product-explanation{
    bottom: 0px;
}

.cRe3Fvf .recomment-product-image {
    width: 100%;
}

.cRe3Fvf .recommend-product-explanation {
    width: 100%;
    background: rgba(0,0,0,0.7);
    color: #fff;
    position: absolute;
    bottom: -100%;
    transition: all ease 0.4s;
    padding: 15px;
}

.cRe3Fvf .recommend-product-text {
    font-size: 13px;
}
.cRe3Fvf .recommend-product-price {
    font-size: 15px;
    margin-top: 10px;
}
.cRe3Fvf .recommend-category-title {
    font-size: 23px;
    font-weight: 600;
    color: #222;
    padding: 10px 0px;
    border-bottom: 3px solid #222;
}
.cRe3Fvf .recommend-category-list {
    margin-top: 10px;
}
.cRe3Fvf .recommend-category-item {
    font-size: 16px;
    color: #9b9b9b;
    padding: 15px 0px;
    position: relative;
}
.cRe3Fvf .recomment-md {
    position: absolute;
    transform: translate(0%, -50%);
    top: 50%;
    left: -70px;
    font-size: 12px;
    color: #166fcc;
    width: 66px;
    height: 26px;
    display: flex;
    padding-left:7px;
    align-items: center;
    background-image: url(/assets/images/md_background.png);
    background-repeat: no-repeat;
    animation: bounce 1s infinite linear;
}

.cRe3Fvf .popular-category-menu {
    display: flex;
    width: 40px;
    flex-direction: column;
    border: 1px solid #ddd;
    justify-content: center;
}

.cRe3Fvf .popular-category-icon {
    color: #ddd;
    padding: 5px 0px;
    text-align: center;
    width: 40px;
    cursor: pointer;
}
.cRe3Fvf .popular-product {
    display: flex;
}
.cRe3Fvf .popular-info-title {
    font-size: 26px;
    font-weight: 600;
}

.cRe3Fvf .popular-info-all {
    font-size: 13px;
    color: #c1c1c1;
    margin: 20px 0px;
}
.cRe3Fvf .popular-info-keyword {
    font-size: 16px;
    color: #c34991;
}
.cRe3Fvf .popular-info-tag {
    padding: 7px;
    background-color: #c34991;
    border-radius: 5px;
    color: #fff;
    font-size: 14px;
    margin: 5px 0px;
}
.cRe3Fvf .popular-product-info {
    display: flex;
    justify-content: space-between;
    flex-direction: column;
    width: 196px;
    padding: 25px;
}

.cRe3Fvf .popular-main-title {
    font-size: 29px;
    text-align: center;
}

.cRe3Fvf .popular-main-explanation {
    font-size: 14px;
    text-align: center;
    margin-top:10px;
}

.cRe3Fvf .popular-main-content {
    padding: 30px;
    background-color: rgba(255,255,255,0.7);
    position: absolute;
    width: 100%;
    bottom:0px;
}

.cRe3Fvf .popular-product-main {
    position:relative;
}
.cRe3Fvf .popular-item-pbox {
    display: flex;
    justify-content: space-between;
    margin-top: 10px;
}

.cRe3Fvf .popular-item-per {
    font-size: 16px;
    color: #ee4848;
    font-weight: 600;
}

.cRe3Fvf .popular-item-price {
    font-size: 16px;
    font-weight: 600;
}

.cRe3Fvf .popular-item-title {
    font-size: 13px;
    color: #a2a2a2;
    height: 30px;
    line-height: 1.2;
}
.cRe3Fvf .popular-item-image {
    text-align: center;
    margin-top: 10px;
}
.cRe3Fvf .popular-product-item {
    width: 33.3333%;
    height: 50%;
    padding: 30px;
}

.cRe3Fvf .popular-product-item {
    border-right: 1px solid #ddd;
}

.cRe3Fvf .popular-product-item:nth-child(1){
    border-bottom: 1px solid #ddd;
}
.cRe3Fvf .popular-product-item:nth-child(2){
    border-bottom: 1px solid #ddd;
}
.cRe3Fvf .popular-product-item:nth-child(3){
    border-bottom: 1px solid #ddd;
}

.cRe3Fvf .popular-product-list {
    flex: 1;
    display: flex;
    flex-wrap: wrap;
}
.cRe3Fvf .popular-product-category {
    margin-right: 10px;
}
.cRe3Fvf .popular-product-body {
    display: flex;
    border: 1px solid #ddd;
}

.cRe3Fvf .popular-category-layer {
    display: flex;
    width: 140px;
    align-items: center;
    z-index: 10;
}

.cRe3Fvf .popular-category-layer:nth-child(1):hover {
    background-color: #78b939;
}

.cRe3Fvf .popular-category-layer:nth-child(2):hover {
    background-color: #2ea6c0;
}

.cRe3Fvf .popular-category-layer:nth-child(3):hover {
    background-color: #3a7ccd;
}

.cRe3Fvf .popular-category-layer:hover .popular-category-hdb {
    display: flex;
}

.cRe3Fvf .popular-category-hdb {
    font-size: 13px;
    margin-left: 15px;
    color: #fff;
    display: none;
}

.cRe3Fvf .tag-purple {
    background-color: #9c26e6;
}

.cRe3Fvf .tag-pink {
    background-color: #ec0075;
}

.cRe3Fvf .tag-green {
    background-color: #009848;
}

.cRe3Fvf .tag-ocher {
    background-color: #d57300;
}

