/*HEADER : S*/
header.header-wrap {
    border-bottom: 1px solid #f2f2f2;
}

header.header-wrap .header-box {
    height: 120px;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

header.header-wrap .header-logo {
    width: 145px;
    height: 100%;
    background-size: 85%;
    background-position: center;
    background-image: url(data:image/svg+xml;base64,);
    background-repeat: no-repeat;
}

header.header-wrap .header-search {
    position: relative;
    padding: 0px 46px;
    height: 36px;
    border: 1px solid #3552DE;
    border-radius: 20px;
    margin: 0px 10px;
    min-width: 500px;
}
header.header-wrap .search-input {
    height: 100%;
    background: none;
    border:none;
}

header.header-wrap .header-search:before {
    position: absolute;
    content: '';
    width: 46px;
    height: 36px;
    left: 0px;
    top: 0px;
    background-position: 65% center;
    background-repeat: no-repeat;
    background-size: auto 65%;
    background-image: url(data:image/svg+xml;base64,);
}

header.header-wrap .header-search:after {
    cursor: pointer;
    position: absolute;
    content: '';
    width: 46px;
    height: 36px;
    right: 0px;
    top: 0px;
    background-position: 45% center;
    background-repeat: no-repeat;
    background-size: auto 65%;
    background-image: url(data:image/svg+xml;base64,);
}

header.header-wrap .header-event {
    display: flex;
    height: 26px;
    align-items: center;
    margin: 0px 10px;
}
header.header-wrap .event-coupon {
    display: flex;
    height: 100%;
    align-items: center;
    padding: 0px 41px 0px 15px;
    color: #3955df;
    border: 1px solid #d1d1d1;
    border-right: none;
    position: relative;
}

header.header-wrap .event-coupon:after {
    width: 26px;
    height: 26px;
    content: '';
    position: absolute;
    right: 0px;
    background-color: #3955df;
    background-repeat: no-repeat;
    background-image: url(data:image/svg+xml;base64,);
    background-size: 13px;
    background-position: center;
}

header.header-wrap .event-coupon-text {
    color: #777;
    font-size: 13px;
    height: 100%;
    display: flex;
    align-items: center;
    padding: 0px 10px;
    width: 140px;
    word-break: keep-all;
}

header.header-wrap .header-member-btns {
    display: flex;
    font-size: 13px;
    color: #777;
}

header.header-wrap .header-member-btn {
    padding: 0px 10px;
}

header.header-wrap .header-member-btn:not(:last-child) {
    border-right: 1px #d1d1d1 solid;
}

header.header-wrap .header-utils {
    display: flex;

}

header.header-wrap .header-ubtn-user {
    margin: 0px 10px;
