index.css:
.mSISAL .main-service {
    background-color: #4b72f3;
    box-sizing: border-box;
    height: 46rem;
    padding: 12rem 15%;
}
.mSISAL .service-wrap {
    display: block;
}
.mSISAL .sub-slogan {
    font-size: 1rem;
    font-weight: 500;
    color: #fff;
    margin-bottom: 1.5rem;
    display: block;
}
.mSISAL .main-title {
    color: #fff;
    font-size: 3rem;
    line-height: 1.25;
    font-weight: 600;
    margin-bottom: 5rem;
    display: block;
}
.mSISAL .api-start-btn {
    box-sizing: border-box;
    display: inline-block;
    padding-bottom: 0.5rem;
    border-bottom: 3px solid #b2c3fb;
    cursor: pointer;
}
.mSISAL .start-btn-text {
    color: #fff;
    font-size: 1.1rem;
    margin-right: 0.5rem;
}
.mSISAL .suggest-service {
    position: relative;
    top:-10rem;
    height: 20rem;
    box-sizing: border-box;
    padding: 0 15%;
}
.mSISAL .suggest-items {
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 1.2rem;
    cursor: pointer;
}
.mSISAL .suggest-item {
    width: calc(25% - 0.9rem);
    height: 100%;
    background-color: #fff;
    box-sizing: border-box;
    padding: 2.5rem 1.5rem;
    border-radius: 1rem;
    box-shadow: 0 20px 60px 0 rgba(73,81,101,.15);
}
.mSISAL .api-icon {
    color: #4b72f3 !important;
    font-size: 2rem;
    margin-bottom: 1rem;
}
.mSISAL .item-type {
    margin-bottom: 0.5rem;
}
.mSISAL .data-type-value {
    display: inline-block;
    box-sizing: border-box;
    padding: 0.4rem 1.3rem;
    background-color: #7649ea;
    color: #fff;
    font-size: 0.8rem;
    border-radius: 1rem;
    margin-bottom: 1rem;
}
.mSISAL .api-name {
    display: block;
    font-size: 1.3rem;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    letter-spacing: -0.025rem;
    font-weight: 500;
    margin-bottom: 0.5rem;
}
.mSISAL .api-desc {
    display: block;
    color: #222;
    font-size: 0.9rem;
    line-height: 1.3;
    margin-bottom: 1.5rem;
}
.mSISAL .api-tag-item {
    font-size: 0.9rem;
    color: #400099;
    margin-right: 0.4rem;
}
.mSISAL .item-type-value {
    color: rgba(0,0,0,.6);
    font-size: 0.9rem;
}
.mSISAL .usage-process {
    position: relative;
    box-sizing: border-box;
    padding: 0 15%;
    padding-bottom: 8rem;
}
.mSISAL .usage-process-texts {
    display: flex;
    justify-content: space-between;
    margin-bottom: 5rem;
}
.mSISAL .process-texts-left {
    width: 50%;
    display: block;
}
.mSISAL .process-texts-subtitle {
    font-size: 1rem;
    font-weight: 500;
    margin-bottom: 1.5rem;
    display: block;
}
.mSISAL .process-texts-maintitle {
    font-size: 3rem;
    line-height: 1.25;
    font-weight: 600;
    margin-bottom: 3rem;
    display: block;
}
.mSISAL .process-texts-desc {
    font-size: 0.9rem;
    font-weight: 400;
    display: block;
    color: #888;
}
.mSISAL .process-texts-right {
    width: 50%;
    display: flex;
    align-items: center;
    justify-content: flex-end;
}
.mSISAL .usage-process-flow {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 1.5rem;
}
.mSISAL .flow-item {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    width: calc(33.33333% - 1rem);
}
.mSISAL .flow-icon-wrap {
    width: 7rem;
    height: 7rem;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 1rem;
    box-shadow: 0 0 15px rgba(0,0,0,0.15);
    margin-bottom: 2rem;
}
.mSISAL .flow-icon {
    color: #4b72f3 !important;
    font-size: 2.5rem;
}
.mSISAL .flow-item-step {
    display: block;
    font-size: 1rem;
    color: #4b72f3 !important;
    font-weight: 400;
    margin-bottom: 1rem;
}
.mSISAL .flow-item-title {
    display: block;
    font-size: 1.7rem;
    font-weight: 500;
    margin-bottom: 1rem;
}
.mSISAL .flow-item-desc {
    text-align: center;
    line-height: 1.4;
    color: #666;
}
.mSISAL .case-service {
    background-color: #e6e9f0;
    padding-top: 10rem;
}
.mSISAL .use-case {
    position: relative;
    box-sizing: border-box;
    padding: 0 15%;
    padding-bottom: 8rem;
}
.mSISAL .use-case-texts {
    display: flex;
    justify-content: space-between;
    margin-bottom: 5rem;
}
.mSISAL .case-texts-left {
    width: 50%;
    display: block;
}
.mSISAL .case-texts-subtitle {
    font-size: 1rem;
    font-weight: 500;
    margin-bottom: 1.5rem;
    display: block;
}
.mSISAL .case-texts-maintitle {
    font-size: 3rem;
    line-height: 1.25;
    font-weight: 600;
    margin-bottom: 3rem;
    display: block;
    white-space: nowrap;
}
.mSISAL .case-texts-desc {
    font-size: 0.9rem;
    font-weight: 400;
    display: block;
    color: #888;
}
.mSISAL .case-texts-right {
    width: 50%;
    display: flex;
    align-items: center;
    justify-content: flex-end;
}
.mSISAL .use-case-items {
    width: 100%;
    height: 17rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 1.2rem;
    box-sizing: border-box;
}
.mSISAL .use-case-item {
    width: calc(33.3333% - 0.8rem);
    height: 100%;
    background-color: #fff;
    box-sizing: border-box;
    padding: 2.5rem 1.8rem;
    box-shadow: 0 20px 60px 0 rgba(73,81,101,.15);
}
.mSISAL .item-logo-wrap {
    display: flex;
    justify-content: flex-start;
    align-items: center;
    margin-bottom: 2rem;
    cursor: pointer;
}
.mSISAL .logo-circle {
    margin-right: 3rem;
}
.mSISAL .logo-sub-desc {
    font-size: 0.9rem;
    font-weight: 400;
    display: block;
    color: #999;
    margin-bottom: 0.9rem;
    word-break: break-all;
}
.mSISAL .logo-text {
    font-size: 1.4rem;
    font-weight: 600;
    display: block;
    color: #222;
    white-space: nowrap;
}
.mSISAL .case-item-desc {
    position: relative;
    padding: 1rem;
}
.mSISAL .item-desc-text {
    line-height: 1.8;
    font-size: 0.9rem;
    font-weight: 400;
    display: block;
    color: #666;
    word-break: keep-all;
    padding-left: 1.2rem;
    position: inherit;
}
.mSISAL .case-icon {
    color: #e3e3e3 !important;
    font-size: 4rem;
    transform: rotate(180deg);
    position: absolute;
    top: -18px;
    left: -5px;
}
.mSISAL .board-service {
    display: flex;
    align-items: center;
}
.mSISAL .notice-board {
    width: 100%;
    padding: 5rem 7rem;
    padding-left: 8rem;
}
.mSISAL .faq-board {
    width: 100%;
    padding: 5rem 7rem;
    padding-right: 8rem;
}
.mSISAL .board-label-text {
    font-size: 1.2rem;
    font-weight: 600;
    color: #2b66a9;
    letter-spacing: -0.02rem;
    line-height: 7rem;
}
.mSISAL .board-item {
    display: flex;
    justify-content: flex-start;
    align-items: center;
    border-bottom: 2px solid #dcdcdc;
    position: relative;
    height: 5rem;
    cursor: pointer;
}
.mSISAL .board-item:hover {
    background-color: #eee;
}
.mSISAL .notice-category {
    display: flex;
    align-items: center;
    margin-right: 1.5rem;
}
.mSISAL .notice-category-text {
    font-size: 1.1rem;
    font-weight: 400;
    letter-spacing: -0.02rem;
}
.mSISAL .board-title {
    display: flex;
    align-items: center;
}
.mSISAL .board-title-text {
    font-size: 1.1rem;
    font-weight: 600;
    color: #2b66a9;
    letter-spacing: -0.02rem;
}
.mSISAL .board-icon-wrap {
    display: flex;
    align-items: center;
}
.mSISAL .board-icon {
    position: absolute;
    right: 0;
    color: #222 !important;
    font-size: 2rem;
    font-weight: 600;
}
.mSISAL .board-btns {
    display: flex;
    justify-content: flex-end;
    align-items: center;
    margin-top: 1.5rem;
}
.mSISAL .board-btn {
    width: 8%;
    cursor: pointer;
    background-color: #4b72f3;
    padding:0 1.5rem;
    height: 2.5rem;
    border-radius: 1.5rem;
    margin-left: 2rem;
}
.mSISAL .board-btn-text {
    color: #fff;
    font-size: 1rem;
    font-weight: 400;
    letter-spacing: -0.02rem;
}
.mSISAL .usr-case-partner {
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 5rem;
}
.mSISAL .partner-icon-wrap {
    width: 2rem;
    display: flex;
    justify-content: center;
    align-items: center;
}
.mSISAL .partner-icon {
    font-size: 2.5rem;
    color: #b7c1ce !important;
    letter-spacing: -0.02rem;
}
.mSISAL .partner-imgs {
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.mSISAL .partner-img-wrap {
    width: 12.5rem;
    height: 5rem;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: 3rem;
}
.mSISAL .partner-img-wrap img {
    width: 100%;
    height: 100%;
}
.mSISAL .partner-img-wrap:last-child {
    margin-right: 0;
}
.mSISAL .logo-circle-icon {
    color: #4b72f3 !important;
    font-size: 3rem;
}

css:
input, select, textarea {
    font-family: inherit;
}
.flex {
    display:flex;
}
.j-c {
    justify-content: center;
}
.j-s {
    justify-content: flex-start;
}
.j-e {
    justify-content: flex-end;
}
.a-c {
    align-items: center;
}
.a-s {
    align-items: flex-start;
}
.a-e {
    align-items: flex-end;
}
.flex-center {
    display:flex;
    justify-content: center;
    align-items: center;
}
.hide {
    display: none;
}
.show {
    display: block;
}
.show-flex {
    display: flex;
}
.common-wrap {
    box-sizing: border-box;
    overflow: auto;
    width: 100vw;
    height: 100vh;
    padding:2rem;
    background-color: #171717;
}
.common-wrap-inner {
    width: 100%;
    height: 100%;
    display: flex;
    align-items: flex-start;
    flex-wrap: wrap;
    gap: 1.2rem;
    overflow: auto;
}
.inner-item {
    display: flex;
    position: relative;
    flex-direction: column;
    width: calc(33.3% - 0.8rem);
    height: 36vh;
    background-color: #f9f9f9;
    border-radius: 1rem;
    cursor: pointer;
    transition: 0.3s ease;
    box-shadow: 0 3px 8px rgb(0 0 0 / 10%);
    overflow: hidden;
}
.start-btn {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 13rem;
  overflow: hidden;
  height: 3rem;
  background-size: 300% 300%;
  backdrop-filter: blur(1rem);
  border-radius: 5rem;
  transition: 0.5s;
  animation: gradient_301 5s ease infinite;
  border: double 4px transparent;
  background-image: linear-gradient(#212121, #212121),  linear-gradient(137.48deg, #ffdb3b 10%,#FE53BB 45%, #8F51EA 67%, #0044ff 87%);
  background-origin: border-box;
  background-clip: content-box, border-box;
}
.stars-text {
    color: #fff;
    font-size: 1.1rem;
}
.container-stars {
  position: absolute;
  z-index: -1;
  width: 100%;
  height: 100%;
  overflow: hidden;
  transition: 0.5s;
  backdrop-filter: blur(1rem);
  border-radius: 5rem;
}
.glow {
  position: absolute;
  display: flex;
  width: 12rem;
}
.circle {
  width: 100%;
  height: 30px;
  filter: blur(2rem);
  animation: pulse_3011 4s infinite;
  z-index: -1;
}
.circle:nth-of-type(1) {
  background: rgba(254, 83, 186, 0.636);
}
.circle:nth-of-type(2) {
  background: rgba(142, 81, 234, 0.704);
}
.start-btn:hover .container-stars {
  z-index: 1;
  background-color: #212121;
}
.start-btn:hover {
  transform: scale(1.1)
}

.start-btn:active {
  border: double 4px #FE53BB;
  background-origin: border-box;
  background-clip: content-box, border-box;
  animation: none;
}

.start-btn:active .circle {
  background: #FE53BB;
}

.stars {
  position: relative;
  background: transparent;
  width: 200rem;
  height: 200rem;
}

.stars::after {
  content: "";
  position: absolute;
  top: -10rem;
  left: -100rem;
  width: 100%;
  height: 100%;
  animation: animStarRotate 90s linear infinite;
}

.stars::after {
  background-image: radial-gradient(#ffffff 1px, transparent 1%);
  background-size: 50px 50px;
}

.stars::before {
  content: "";
  position: absolute;
  top: 0;
  left: -50%;
  width: 170%;
  height: 500%;
  animation: animStar 60s linear infinite;
}

.stars::before {
  background-image: radial-gradient(#ffffff 1px, transparent 1%);
  background-size: 50px 50px;
  opacity: 0.5;
}

@keyframes animStar {
  from {
    transform: translateY(0);
  }

  to {
    transform: translateY(-135rem);
  }
}

@keyframes animStarRotate {
  from {
    transform: rotate(360deg);
  }

  to {
    transform: rotate(0);
  }
}

@keyframes gradient_301 {
  0% {
    background-position: 0% 50%;
  }

  50% {
    background-position: 100% 50%;
  }

  100% {
    background-position: 0% 50%;
  }
}

@keyframes pulse_3011 {
  0% {
    transform: scale(0.75);
    box-shadow: 0 0 0 0 rgba(0, 0, 0, 0.7);
  }

  70% {
    transform: scale(1);
    box-shadow: 0 0 0 10px rgba(0, 0, 0, 0);
  }

  100% {
    transform: scale(0.75);
    box-shadow: 0 0 0 0 rgba(0, 0, 0, 0);
  }
}

