}
.Haa_ao .apis-wrap {
    box-sizing: border-box;
    margin: 3rem 25%;
}
/*apis body tab*/
.Haa_ao .apis-tab-wrap {
    width: 100%;
    height: 3.2rem;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    margin-bottom: 3.5rem;
} 
.Haa_ao .apis-tab {
    width: 10rem;
    height: 100%;
    border: 1px solid #dddddd;
    box-sizing: border-box;
    display: flex;
    justify-content: center;
    align-items: center;
}
.Haa_ao .apis-tab.selected {
    border: 2px solid #4b72f3 !important;
}
.Haa_ao .apis-tab:not(:first-child) {
    border-left: none;
}
.Haa_ao .selected .apis-tab-text {
    color: #4b72f3 !important;
    font-weight: 600 !important;
}
.Haa_ao .selected .apis-tab-number {
    color: #4b72f3 !important;
    font-weight: 600 !important;
}
.Haa_ao .apis-tab-text {
    font-size: 1rem;
    color: #979797;
    letter-spacing: -0.02rem;
}
.Haa_ao .apis-tab-number {
    font-size: 1rem;
    color: #979797;
    letter-spacing: -0.02rem;
    margin-left: 0.3rem;
}
.Haa_ao .apis-tab:hover .apis-tab-text {
    font-weight: 400;
    color: #0e1b45;
}
.Haa_ao .apis-tab:hover .apis-tab-number {
    font-weight: 400;
    color: #0e1b45;
}
/*pagenation*/
.Haa_ao .apis-pagenation {
    width: 100%;
    height: 2rem;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-top: 2rem;
}
.Haa_ao .pagenation-icon {
    font-size: 1.2rem;
    color: #8b8b8b !important;
    letter-spacing: -0.02rem;
    font-weight: 500;
    margin: 0 1.2rem;
    box-sizing: border-box;
    cursor: pointer;
}
.Haa_ao .pagenation-icon[disabled="true"] {
    opacity: 0.5;
    cursor: default;
}

.Haa_ao .pagenation-text {
    font-size: 0.9rem;
    color: #4b4b4b;
    letter-spacing: -0.02rem;
    font-weight: 500;
    width: 1.5rem;
    height: 1.5rem;
    margin: 0 1rem;
    box-sizing: border-box;
    cursor: pointer;
    display: flex;
    justify-content: center;
    align-items: center;
}
.Haa_ao .pagenation-text.selected {
    border: 1px solid #222;
    border-radius: 5rem;
    border: 1px solid #222;
    border-radius: 1rem;
    width: 1.5rem;
    height: 1.5rem;
    display: flex;
    justify-content: center;
    align-items: center;
}
/*body contents*/
.Haa_ao .apis-contents-wrap {
    width: 100%;
    height: 100%;
    box-sizing: border-box;
}
.Haa_ao .apis-contents-items {
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    box-sizing: border-box;
    flex-wrap: wrap;
    border-top: 1px solid #dddddd;
    border-bottom: 1px solid #dddddd;
}
.Haa_ao .contents-item {
    border-bottom: 1px solid #dddddd;
    width: 25%;
    height: 100%;
    background-color: #fefefe;
    box-sizing: border-box;
    padding: 2.5rem 1.5rem;
    transition: background-color 0.35s ease,box-shadow 0.35s ease;
    word-break: keep-all;
    cursor:pointer;
}
.Haa_ao .contents-item.none-bottom {
    border-bottom: none;
}
.Haa_ao .contents-item:hover {
    background-color: #fff;
    box-shadow: 0 20px 60px 0 rgba(73,81,101,.15);
    z-index: 1;
}
.Haa_ao .item-type {
    margin-bottom: 0.5rem;
}
.Haa_ao .data-type-value {
    display: inline-block;
    box-sizing: border-box;
    padding: 0.4rem 1.3rem;
    background-color: #7649ea;
    color: #fff;
    font-size: 0.8rem;
    border-radius: 1rem;
    margin-bottom: 1rem;
}
.Haa_ao .apis-name {
    display: block;
    font-size: 1.3rem;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    letter-spacing: -0.025rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
}
.Haa_ao .apis-desc {
    display: block;
    color: #666;
    font-size: 0.9rem;
    line-height: 1.3;
    margin-bottom: 1rem;
    height: 70px;
    overflow-y: auto;
}

.Haa_ao .apis-tags {
    display: flex;
    flex-wrap: wrap;
    line-height: 22px;
    height: 50px;
    overflow: hidden;
}   

.Haa_ao .apis-tag-item {
    font-size: 0.9rem;
    color: #8a6ab5;
    margin-right: 0.4rem;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
.Haa_ao .item-logo  {
    display: flex;
    justify-content: flex-start;
    align-items: center;
    margin-bottom: 1.5rem;
    display: none;
}
.Haa_ao .item-logo-img {
    width: 4rem;
    height: 2rem;
}
.Haa_ao .item-type-value {
    color: rgba(0,0,0,.6);
    font-size: 0.9rem;
}

apis_detail.css:
/*body top*/
.jGvPt6 .dev-top-wrap {
