    justify-content: center;
    align-content: center;
    flex-direction: column;
}

.jGvPt6 .dev-top-wrap.dataset {
    height: 13rem;
}

.jGvPt6 .dev-top-contents {
    display: flex;
    justify-content: flex-start;
    align-items: start;
    width: 100%;
    height: 100%;
}
.jGvPt6 .top-contents-wrap {
    width: 100%;
    height: 4rem;
    display: flex;
