.jGvPt6 .table-row-download {
    width: 6.25rem;
    padding: 0 1.25rem;
}

.jGvPt6 .info-tcol-explanation,
.jGvPt6 .table-row-explanation {
    width: calc(100% - 22.55rem);
    height: 2.4rem;
    padding: 0 1.25rem;
}

.jGvPt6 .lds-ellipsis.show {
    display: inline-block
}

.jGvPt6 .lds-ellipsis {
  display: none;
  position: absolute;
  width: 5rem;
  height: 5rem;
  top:50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

.jGvPt6 .request-editor-box {
    /*position: relative;*/
    display: flex;
    height: 20rem;
}
.jGvPt6 .response-editor-box {
    display: flex;
    height: calc(100% - 47rem);
}
.jGvPt6 .lds-ellipsis div {
  position: absolute;
  top: 2.1rem;
  width: 0.8rem;
  height: 0.8rem;
  border-radius: 50%;
  background: #fff;
  animation-timing-function: cubic-bezier(0, 1, 1, 0);
}
.jGvPt6 .lds-ellipsis div:nth-child(1) {
  left: 0.5rem;
  animation: lds-ellipsis1 0.6s infinite;
}
.jGvPt6 .lds-ellipsis div:nth-child(2) {
  left: 0.5rem;
  animation: lds-ellipsis2 0.6s infinite;
}
.jGvPt6 .lds-ellipsis div:nth-child(3) {
  left: 2rem;
  animation: lds-ellipsis2 0.6s infinite;
}
.jGvPt6 .lds-ellipsis div:nth-child(4) {
  left: 3.5rem;
  animation: lds-ellipsis3 0.6s infinite;
}

.jGvPt6 .dataset-download-button {
    box-shadow: 6px 6px 5px 0px rgba(52,85,157,.1);
    border-radius: 5rem;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0.4rem 3rem;
    background-color: #222;
    color: #fff;
}

@keyframes lds-ellipsis1 {
  0% {
    transform: scale(0);
  }
  100% {
    transform: scale(1);
  }
}
@keyframes lds-ellipsis3 {
  0% {
    transform: scale(1);
  }
  100% {
    transform: scale(0);
  }
}
@keyframes lds-ellipsis2 {
  0% {
    transform: translate(0, 0);
  }
  100% {
    transform: translate(1.5rem, 0);
  }
}

.jGvPt6 .request-control-layer {
    display: flex;
    gap: 0.5rem;
}
