    font-weight: 500;
}
.header-btn.register {
    cursor: pointer;
    background-color: #fff;
    padding:0 1.5rem;
    height: 2.5rem;
    border-radius: 1.5rem;
    margin-left: 2rem;
}
.header-btn.register .btn-text {
    color: #476ff3;
    font-size: 1.1rem;
    font-weight: 600;
}
.main-service {
    background-color: #4b72f3;
    box-sizing: border-box;
    height: 46rem;
    padding: 12rem 15%;
}
.service-wrap {
    display: block;
}
.sub-slogan {
    font-size: 1rem;
    font-weight: 500;
    color: #fff;
    margin-bottom: 1.5rem;
    display: block;
}
.main-title {
    color: #fff;
    font-size: 3rem;
    line-height: 1.25;
    font-weight: 600;
    margin-bottom: 5rem;
    display: block;
}
.api-start-btn {
