    height: 1.625rem;
    display: flex;
    font-size: 0.75rem;
    min-width: 3.4rem;
    align-items: center;
    font-weight: 400;
    line-height: 1.2;
    margin-right: 0.5rem;
    border-radius: 0 0.5rem 0.5rem 0.5rem;
    justify-content: center;
}
.contents-line-text {
    display: flex;
    word-break: break-all;
    align-items: center;
    font-size: 1rem;
    letter-spacing: -0.03rem;
    color: #444;
}
.table-column {
    display: flex;
    justify-content: flex-start;
    align-items: center;
    height: 3rem;
    box-sizing: border-box;
    background-color: #f2f4f5;
    border-radius: 0.75rem 0.75rem 0 0;
    padding: 0 1rem;
}
.table-row {
    display: flex;
    justify-content: flex-start;
    align-items: center;
    height: 3rem;
    box-sizing: border-box;
    border-bottom: 1px solid #e4e8f1;
    padding: 0 1rem;
}
.table-testinfo-row {
    display: flex;
    justify-content: flex-start;
    align-items: center;
    height: 2.4rem;
    box-sizing: border-box;
    border-bottom: 1px solid #e4e8f1;
    padding: 0 1rem;
}
.table-column-key,
.table-column-value,
.table-column-description {
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    padding: 0 1rem;
}
.table-row-key,
.table-row-value,
.table-row-description {
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    overflow: hidden;
    padding: 0 1rem;
}
.table-column-type {
    width: 30%;
    height: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    padding: 0 1rem;
}
.table-row-type {
    width: 30%;
    height: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    overflow: hidden;
    padding: 0 1rem;
}
.table-column-required {
    width: 60%;
    height: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    padding: 0 1rem;
}
.table-row-required {
    width: 60%;
    height: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    overflow: hidden;
    padding: 0 1rem;
}
.table-column-text {
    color: #858992;
    font-size: 0.9rem;
    line-height: 1.4;
    font-weight: 600;
}
.table-row-text{
    color: #444;
    font-size: 0.9rem;
    line-height: 1;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    border: none;
}
.box-contents-table {
    margin-top: 0.5rem;
}
.table-row-input{
    width: 100%;
    height: 100%;
    color: #444;
    font-size: 0.9rem;
    line-height: 1;
    border: none;
    box-sizing: border-box;
    padding: 1rem 0;
}
.table-column-checkbox {
    width: 3%;
    height: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    padding: 0 1rem;
}
.table-row-checkbox {
    width: 3%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    overflow: hidden;
    padding: 0 1rem;
}
.table-column-icon {
    width: 5rem;
    height: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    padding: 0 1rem;
}
.table-row-icon {
    width: 5rem;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    overflow: hidden;
    padding: 0 1rem;
}
.contents-add-wrap {
    display: flex;
    align-items: center;
    width: 5rem;
    height: 100%;
    box-sizing: border-box;
    padding: 0 2rem;
    justify-content: center;
    transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms,box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms,border 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms;
}
.contents-add-wrap:hover {
    border-radius: 1.5rem;
    background-color: #dae7f0;
}
.add-icon-box {
    width: 1rem;
    height: 1rem;
    background-color: rgb(59 151 227);
    border-radius: 0.2rem;
    box-sizing: border-box;
    padding: 0.3rem 0.3rem;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: 0.6rem;
}
.row-add-icon {
    font-size: 0.8rem;
    font-weight: 600;
}
.add-label-text {
    font-size: 1rem;
    font-weight: 500;
    color: rgb(59 151 227);
    letter-spacing: -0.03rem;
}
.delete-icon-box {
    width: 2rem;
    height: 80%;
    background-color: #f2f4f5;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 0.4rem;
}
.row-delete-icon {
    font-size: 1rem;
    font-weight: 600;
    color: #999 !important;
}
.request-control-group {
    display: flex;
    justify-content: flex-start;
    align-items: center;
    padding: 0.6rem 0.3rem 0;
    margin-bottom: 0.5rem;
}
.group-item {
    height: 1.5rem;
    margin-right: 0.5rem;
}
.group-item.active{
    box-sizing: border-box;
    border-bottom: 2px solid #434852;
}
.group-item-text {
    color: #858992;
    border: none;
    font-size: 1rem;
    font-weight: 600;
    line-height: 1.4rem;
    border-radius: 0;
    background-color: transparent;
    box-sizing: border-box;
}
.active .group-item-text  {
    color: #434852;
}
.group-item-btn {
    width: 100%;
    height: 80%;
    display: flex;
    justify-content: center;
    align-items: center;
}
.group-item-btn:hover {
    background-color: #d6d8de;
    border-radius: 0.5rem;
}
.request-editor {
    width: 100%;
    height: 35rem;
    background-color: #888;
}
.request-btn-wrap {
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    box-sizing: border-box;
    padding: 0.5rem 0;
    margin-top: 0.5rem;
}
.request-btn {
    cursor: pointer;
    background-color: #f7f7f7;
    padding:0 1.5rem;
    height: 2rem;
    border-radius: 0.5rem;
    display: flex;
    justify-content: center;
    align-items: center;
}
.request-btn-text {
    font-size: 1rem;
    letter-spacing: -0.03rem;
    color: #476ff3;
    font-weight: 600;
}

