.company-tab-bar {
    height: 3.1rem;
    left: 15.3rem;
    position: absolute;
    top: 0;
    width: 15.3rem;
}
.company-tab-bar:after,.company-tab-bar:before {
    background: #b9b9b9;
    border-radius: 1px;
    content: "";
    height: 0.8rem;
    position: absolute;
    top: calc(50% - 0.4rem);
    width: 1px;
    z-index: 10;
}
.company-tab-bar:before {
    left: -1px;
}
.company-tab-bar:after {
    right: 1px;
}
.company-tab-current {
    border-radius: 26px;
    box-shadow: 0 0 0 1px #400099;
    box-sizing: border-box;
    height: 50px;
    left: 0;
    position: absolute;
    top: 0;
    transition: transform .4s ease;
    width: 33.3333333333%;
    z-index: 10
}

.company-tab-input:first-child:checked~.company-tab-current {
    transform: translateX(0);
}

.company-tab-input:nth-child(3):checked~.company-tab-current {
    transform: translateX(100%);
}

.company-tab-input:nth-child(5):checked~.company-tab-current {
    transform: translateX(200%)
}

.company-tab-input:first-child:checked~.company-tab-bar:before,.company-tab-input:nth-child(3):checked~.company-tab-bar:after,.company-tab-input:nth-child(3):checked~.company-tab-bar:before,.company-tab-input:nth-child(5):checked~.company-tab-bar:after {
    display: none
}

