order_dashboard.css:
.order-dashboard {
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #dde0e7;
}

.wifi-info {
    width: 80%;
    display: flex;
    justify-content: flex-end;
    margin-bottom: 1rem;
}

.info-wrap {
    display: flex;
    justify-content: center;
    align-items: center;
}

.wrap-label {
    color: #4750db;
    font-size: 1.3rem;
    margin-right: 1rem;
    font-weight: 600;
}

.info-wrap img {
    margin-right: 0.5rem;
}

.wrap-text {
    font-size: 1.3rem;
    font-weight: 500;
    color: #222;
}

.promotion-video {
    width: 80%;
    height: 75%;
    position: relative;
}

.video-element {
    position: relative;
    z-index: 0;
    width: 100%;
    height: 100%;
}

.video-thumb {
    position: absolute;
    top: 0;
    left: 0;
    box-sizing: border-box;
    z-index: 1;
    width: 100%;
    height: 100%;
    border: 0.7rem solid #fff;
    background-color: #d1d1d1;
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
}

.promotion-video img {
    position: absolute;
    z-index: 2;
    width: 10rem;
    left: calc(50% - 5rem);
    top: calc(50% - 5rem);
    cursor: pointer;
}

.welcome-text {
    margin-top: 1rem;
    margin-bottom: 1.5rem;
}

.welcome-text span {
    font-size: 1.2rem;
    color: #a5a5a7;
}

.running-info {
    display: flex;
}

.open-info {
    display: flex;
    justify-content: center;
    align-items: center;
}

.info-label {
    font-size: 1.2rem;
    color: #a5a5a7;
    margin-right: 1rem;
}

.info-value {
    font-size: 1.2rem;
    color: #222;
}

.info-seperator {
    width: 1px;
    height: 100%;
    background: #d1d1d1;
    margin: 0 2rem;
}

css:
html { font-size: 13px !important; }

.smartluna {
    width: 100vw;
    height: 100vh;
    position: relative;
    display: flex;
}

.smartluna * {
    user-select: none;
}

.sidemenu {
    width: 250px;
    min-width: 250px;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    position: relative;
    box-shadow: 0 0 10px rgba(0,0,0,0.2);
}

.sidemenu-logo {
    width: 100%;
    text-align: center;
    padding: 4rem 0;
}

.sidemenu-logo img {
    height: 6rem;
    width: auto;
}

.sidemenu-items {
    width: 100%;
    height: 100%;
    max-height: calc(100% - 29rem);
    overflow: auto;
}

.items-menu {
    display: flex;
    width: 100%;
    height: 4rem;
    justify-content: center;
    align-items: center;
    cursor: pointer;
}

.items-menu.selected {background-color: #00bfaa;}

.items-menu.selected span {
    color: #fff;
}

.items-menu span {
    font-size: 1.4rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
    color: #333;
}

.sidmenu-call {
    box-sizing: border-box;
    width: 100%;
    height: 5rem;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0.5rem 2rem;
    position: absolute;
    bottom: 2rem;
}

.sidmenu-date {
    width: 100%;
    height: 5rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    position: absolute;
    bottom: 8.5rem;
}

.call-btn {
    display: flex;
    box-sizing: border-box;
    width: 100%;
    height: 100%;
    background-color: #6e778d;
    border-radius: 3rem;
    align-items: center;
    padding: 0.5rem;
}

.call-circle {
    width: 3rem;
    height: 3rem;
    background-color: #fff;
    border-radius: 3rem;
    margin-right: 0.8rem;
    display: flex;
    justify-content: center;
    align-items: center;
}

.call-btn span {
    color: #fff;
    font-size: 1.4rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
}

.date-day {
    width: auto;
    padding: 0.8rem 0;
    border-bottom: 1px solid #d1d1d1;
}

.date-day span {
    font-size: 1.3rem;
}

.date-time {
    width: auto;
    padding: 0.8rem 0;
}

.date-time span {
    font-size: 1.8rem;
    font-weight: 600;
}

.contents {
    width: 100%;
    max-width: calc(100% - 250px);
    height: 100%;
}

