    justify-content: flex-start;
    align-items: center;
}

.HQpTyi .infos-desc span {
    font-size: 1rem;
    color: #777;
}

.HQpTyi .infos-price {
    width: 100%;
    height: 2rem;
    display: flex;
    justify-content: flex-end;
    align-items: flex-end;
}

.HQpTyi .infos-price span {
    font-size: 1.8rem;
    letter-spacing: -0.07rem;
    font-weight: 500;
}

.HQpTyi .infos-price span.price-unit {
    font-size: 1.4rem;
    font-weight: 600;
    margin-left: 0.1rem;
    padding-bottom: 0.1rem;
}

