    font-size: 1.3rem;
}

.IasfLc .order-btn {
    width: 60%;
    height: 100%;
    border-radius: 0.5rem;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-left: 0.5rem;
    background-color: #4157b9;
}

.IasfLc .order-btn span {
    color: #fff;
    font-weight: 500;
    font-size: 1.3rem;
}

.HQpTyi .added-item {
    width: 100%;
