}

.HQpTyi .added-item-name {
    width: 100%;
    margin-bottom: 0.3rem;
    display: flex;
}

.HQpTyi .added-item-name span {
    font-size: 1.5rem;
    font-weight: 600;
}

.HQpTyi .added-item-price {
    width: 100%;
    margin-bottom: 1rem;
    display: flex;
    justify-content: flex-end;
}

.HQpTyi .added-item-price span {
    font-size: 1.7rem;
    letter-spacing: -0.05rem;
    font-weight: 600;
} 

