manager_orders.css:
.confirm-card.click-disable {
pointer-events: none;
}
 .cancel-item {
color:red;
text-decoration:line-through;
} .confirm-order-list, .confirm-order-list * {
    box-sizing: border-box;
}

.confirm-order-list {
    width: 100%;
    height: 100%;
    padding: 3rem;
    display: block;
    gap: unset;
    flex-wrap: unset;
}

.confirm-order-list .slick-slide > div {
    padding: 0.5rem;
    display: flex;
    gap: 1rem;
}

.confirm-card {
    display: flex !important;
    position: relative;
    flex-direction: column;
    width: calc(33.333% - (4rem/3));
    font-size: 16px;
    height: 44vh;
    background-color: rgb(255 255 255 / 70%);
    border-radius: 0.6rem;
    border: 1px solid var(--theme-bg-color);
    cursor: pointer;
    transition: 0.3s ease;
    overflow: hidden;
    box-sizing: border-box;
}

.bottom-text {
    width: 100%;
    text-align: center;
    padding: 2rem 1rem;
    box-sizing: border-box;
    position: absolute;
    bottom: 0px;
    color: #868686;
}

.confirm-card .table-name-block {
    width: 100%;
    height: 4rem;
    min-height: 4rem;
    display: flex;
    justify-content: space-between;
    padding: 2rem 1.5rem 0 1.5rem;
    align-items: center;
    margin-bottom: 0.8rem;
    box-sizing: border-box;
}

.confirm-card .table-name-text {
    font-size: 4rem;
    color: #e4e4e4;
    font-weight: 600;
    letter-spacing: -0.025rem;
}

.table-order-utils {
    display: flex;
    gap: 1rem;
}

.confirm-card .table-status, 
.confirm-card .table-order-time{
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0.6rem 1rem;
    background: #455cc2;
    border-radius: 1.5rem;
}

.confirm-card .table-order-time {
    background: #797979;
}

.confirm-card .table-status-sign,
.confirm-card .table-time-sign {
    width: 0.8rem;
    height: 0.8rem;
    border-radius: 1rem;
    background-color: #fff;
    margin-right: 0.3rem;
}

.confirm-card .table-status-msg,
.confirm-card .table-time-msg{
    font-size: 1.2rem;
    color: #fff;
}

.confirm-card .orders-ul {
    width: 100%;
    height: calc(100% - 7.5rem);
    padding: 0.8rem 1.8rem 0 1.8rem;
    overflow: auto;
}

.confirm-card .orders-li {
    display: flex;
    width: 100%;
    padding: 1rem 0;
    justify-content: space-between;
}

.confirm-card .orders-li:not(:last-child) {
    border-bottom: 1px #e8e8e8 solid;
}

.confirm-card .orders-li-name {
    font-size: 1.3rem;
    letter-spacing: -0.03rem;
    font-weight: 500;
}

.confirm-card .orders-li-count {
    font-size: 1.3rem;
}

.confirm-card .table-btns {
    display: flex;
    width: 100%;
    height: 3.5rem;
}

.confirm-card .table-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    background-color: #222;
    color: #fff;
    border-right: 1px solid #fff;
}

.confirm-card .table-btn span {
    font-size: 1.1rem;
}

.confirm-card .table-btn:last-child {
    border-right: 0;
}

.confirm-contents {
    width: calc(100% - 250px);
    background-color: #e7ecf0;
    overflow-y: auto;
}

.confmenu-tab {
    box-sizing: border-box;
    width: 100%;
    height: 5rem;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0.5rem 2rem;
}

.confmenu-tab .tab-btn {
    display: flex;
    box-sizing: border-box;
    width: 100%;
    height: 100%;
    background-color: #00cfbb;
    border-radius: 3rem;
    align-items: center;
    padding: 0.5rem;
}

.confmenu-tab .tab-btn.active {
    background-color: #454dda;
}

.confmenu-tab .tab-circle {
    width: 3rem;
    height: 3rem;
    background-color: #fff;
    border-radius: 3rem;
    margin-right: 0.8rem;
    display: flex;
    justify-content: center;
    align-items: center;
}

.confmenu-tab .tab-circle img {
    height: 70%;
}

.confmenu-tab .tab-btn span {
    color: #fff;
    font-size: 1.4rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
}

.confmenu-items {
    width: 100%;
    height: 100%;
    max-height: calc(100% - 29rem);
    overflow: auto;
    display: flex;
    flex-direction: column;
    justify-content: center;
}

.confmenu-date {
    width: 100%;
    height: 5rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

.confmenu-logo {
    width: 100%;
    text-align: center;
    padding: 4rem 0 2rem 0;
}

.confirm-date-day {
    color: #757576;
}

#manager_orders_list .slick-prev, #manager_orders_list .slick-next {
    width:40px;
    height:40px;
}

#manager_orders_list .slick-prev {
    left: 10px;
}

#manager_orders_list .slick-next {
    right: 10px;
}

#manager_orders_list .slick-prev:before,
#manager_orders_list .slick-next:before
{
    font-family: 'slick';
    font-size: 40px;
    line-height: 1;

    opacity: .95;
    color: white;

    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

#manager_orders_list  .slick-initialized .slick-slide {
    display: flex;
    flex-direction: column;
}

