}

.IasfLc .order-btn span {
    color: #fff;
    font-weight: 500;
    font-size: 1.3rem;
}

.HQpTyi .added-item {
    width: 100%;
    display: flex;
    flex-direction: column;
    padding-bottom: 1.5rem;
    border-bottom: 1px solid #d1d1d1;
    margin-bottom: 1.5rem;
}

.HQpTyi .added-item-name {
    width: 100%;
    margin-bottom: 0.3rem;
    display: flex;
}

.HQpTyi .added-item-name span {
    font-size: 1.5rem;
    font-weight: 600;
}

.HQpTyi .added-item-price {
    width: 100%;
    margin-bottom: 1rem;
    display: flex;
    justify-content: flex-end;
}

.HQpTyi .added-item-price span {
    font-size: 1.7rem;
    letter-spacing: -0.05rem;
    font-weight: 600;
} 

.HQpTyi .added-item-controls {
    position: relative;
    box-sizing: border-box;
    display: flex;
    width: 100%;
    height: 3rem;
    justify-content: space-between;
}

.HQpTyi .controls-minus, .HQpTyi .controls-plus {
    width: 3rem;
    min-width: 3rem;
    height: 3rem;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #4157b9;
    border-radius: 0.3rem;
}

.HQpTyi .controls-trash {
    width: 3rem;
    min-width: 3rem;
    height: 3rem;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #6f778e;
    border-radius: 0.3rem;
}

.HQpTyi .controls-minus span, .HQpTyi .controls-plus span {
    font-size: 2rem;
    line-height: 1;
    display: flex;
    color: #fff;
    margin-bottom:0.1rem;
}

.HQpTyi .controls-input {
    width: 30%;
    border:0;
    font-size: 1.8rem;
    font-weight: 500;
    color: #4157b9;
    text-align: center;
}

manager_orders.css:
.confirm-order-list, .confirm-order-list * {
    box-sizing: border-box;
