}

.DsKVdm .detail-quantity {
    width: 15%;
    height: auto;
    display: flex;
    justify-content: center;
    align-items: center;
}

.DsKVdm .detail-price {
    width: 25%;
    height: auto;
    display: flex;
    justify-content: end;
    align-items: center;
}

.DsKVdm .price-value {
    font-size: 1.5rem;
    font-weight: 500;
    letter-spacing: -0.04rem;
    margin-right: 0.2rem;
}

.DsKVdm .price-label {
    font-size: 1.5rem;
    font-weight: 500;
    letter-spacing: -0.04rem;
}

.lyQxQd .sum-text span {
    font-size: 1.7rem;
    font-weight: 700;
    letter-spacing: -0.04rem;
    color: #4157b9;
}

.list-sum.lyQxQd{
    width: 100%;
    height: 3rem;
    display: flex;
    align-items: center;
}

.bill-btns {
    width: 80%;
    height: 5rem;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-top: 1rem;
}

.lyQxQd .sum-quantity span {
    font-size: 1.5rem;
    font-weight: 700;
    letter-spacing: -0.04rem;
    color: #4157b9;
}

.lyQxQd .price-value,
.lyQxQd .price-label {
    font-size: 1.5rem;
    font-weight: 700;
    letter-spacing: -0.04rem;
    color: #4157b9;
}

.lyQxQd .sum-text {
    width: 60%;
    height: 100%;
    display: flex;
    align-items: center;
}

.lyQxQd .sum-quantity {
    width: 15%;
    height: auto;
    display: flex;
    justify-content: center;
    align-items: center;
}

.lyQxQd .sum-price {
    width: 25%;
    height: auto;
    display: flex;
    justify-content: end;
    align-items: center;
    padding-right: 0.5rem;
}

.items-detail.cancel .text-layer {
