    height: 5rem;
    border-radius: 0.5rem;
    border: 2px solid #8a8a8a;
    padding: 0.5rem 0.7rem;
}
.CcSbFu .setting-input {
    width: 100%;
    height: 3rem;
    border-radius: 0.5rem;
    border: 2px solid #8a8a8a;
    padding: 0.5rem 0.7rem;
}
.CcSbFu .setting-input.readonly {
    color: #a8a8a8;
    background-color: #f2f2f2;
    border: 1px solid #a8a8a8;
}
.CcSbFu .setting-textarea.readonly {
    color: #a8a8a8;
    background-color: #f2f2f2;
    border: 1px solid #a8a8a8;
}
.CcSbFu .setting-input.required, 
.CcSbFu .setting-input:hover {
    border: 2px solid #1e59f1;
}
.CcSbFu .btn-type-func {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0 2rem;
    background-color: #00a5b9;
    border-radius: 0.4rem;
    margin-left: 0.5rem;
    height: 3rem;
    cursor: pointer;
    border: none;
}
.CcSbFu .btn-type-normal {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0 2rem;
    background-color: #d3d7e4;
    border-radius: 0.4rem;
    height: 3rem;
    cursor: pointer;
    border: none;
}
.CcSbFu .btn-func-text {
    font-size: 1.2rem;
    color: #fff;
    letter-spacing: -0.02rem;
}
.CcSbFu .btn-normal-text {
    font-size: 1.2rem;
    color: #32364f;
    letter-spacing: -0.02rem;
}
.CcSbFu .content-button-wrapper {
    display: flex;
    justify-content: flex-end;
    align-items: center;
}
.CcSbFu .setting-label.required {
    display: flex;
    align-items: center;
}
.CcSbFu .setting-label.required::after, .required-label::after {
    content: '';
    width: 0.4rem;
    height: 0.4rem;
    border-radius: 100%;
    background-color: #1e59f1;
    margin-left: 0.2rem;
}
.CcSbFu .height-auto {
    height: auto !important;
}
