order_dashboard.css:
.order-dashboard {
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #dde0e7;
}

.wifi-info {
    width: 80%;
    display: flex;
    justify-content: flex-end;
    margin-bottom: 1rem;
}

.info-wrap {
    display: flex;
    justify-content: center;
    align-items: center;
}

.wrap-label {
    color: #4750db;
    font-size: 1.3rem;
    margin-right: 1rem;
    font-weight: 600;
}

.info-wrap img {
    margin-right: 0.5rem;
}

.wrap-text {
    font-size: 1.3rem;
    font-weight: 500;
    color: #222;
}

.promotion-video {
    width: 80%;
    height: 75%;
    position: relative;
}

.video-element {
    position: relative;
    z-index: 0;
    width: 100%;
    height: 100%;
}

.video-thumb {
    position: absolute;
    top: 0;
    left: 0;
    box-sizing: border-box;
    z-index: 1;
    width: 100%;
    height: 100%;
    border: 0.7rem solid #fff;
    background-color: #d1d1d1;
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
}

.promotion-video img {
    position: absolute;
    z-index: 2;
    width: 10rem;
    left: calc(50% - 5rem);
    top: calc(50% - 5rem);
    cursor: pointer;
}

.welcome-text {
    margin-top: 1rem;
    margin-bottom: 1.5rem;
}

.welcome-text span {
    font-size: 1.2rem;
    color: #a5a5a7;
}

.running-info {
    display: flex;
}

.open-info {
    display: flex;
    justify-content: center;
    align-items: center;
}

.info-label {
    font-size: 1.2rem;
    color: #a5a5a7;
    margin-right: 1rem;
}

.info-value {
    font-size: 1.2rem;
    color: #222;
}

.info-seperator {
    width: 1px;
    height: 100%;
    background: #d1d1d1;
    margin: 0 2rem;
}

