.smartluna {
    width: 100vw;
    height: 100vh;
    position: relative;
    display: flex;
}

.sidemenu {
    width: 250px;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    position: relative;
    box-shadow: 0 0 10px rgba(0,0,0,0.2);
}

.sidemenu-logo {
    width: 100%;
    text-align: center;
    padding: 4rem 0;
}

.sidemenu-logo img {
    height: 6rem;
    width: auto;
}

.sidemenu-items {
    width: 100%;
    height: 100%;
    max-height: calc(100% - 29rem);
    overflow: auto;
}

.items-menu {
    display: flex;
    width: 100%;
    height: 4rem;
    justify-content: center;
    align-items: center;
}

.items-menu.selected {background-color: #00bfaa;}

.items-menu.selected span {
    color: #fff;
}

.items-menu span {
    font-size: 1.4rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
    color: #333;
}

.sidmenu-call {
    box-sizing: border-box;
    width: 100%;
    height: 5rem;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0.5rem 2rem;
    position: absolute;
    bottom: 2rem;
}

.sidmenu-date {
    width: 100%;
    height: 5rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    position: absolute;
    bottom: 8.5rem;
}

.call-btn {
    display: flex;
    box-sizing: border-box;
    width: 100%;
    height: 100%;
    background-color: #6e778d;
    border-radius: 3rem;
    align-items: center;
    padding: 0.5rem;
}

.call-circle {
    width: 3rem;
    height: 3rem;
    background-color: #fff;
    border-radius: 3rem;
    margin-right: 0.8rem;
}

.call-btn span {
    color: #fff;
    font-size: 1.4rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
}

.date-day {
    width: auto;
    padding: 0.8rem 0;
    border-bottom: 1px solid #d1d1d1;
}

.date-day span {
    font-size: 1.3rem;
}

.date-time {
    width: auto;
    padding: 0.8rem 0;
}

.date-time span {
    font-size: 1.8rem;
    font-weight: 600;
}
