main.css:
:root {
  --page-max-width: 1600px;
  --sub-max-width: 1100px;
  --main-bg-img: url(https://static.wixstatic.com/media/249619_72a11ad49f664be98321be41718cb0cf~mv2.jpg/v1/fill/w_1912,h_1104,al_b,q_90,usm_0.66_1.00_0.01,enc_auto/249619_72a11ad49f664be98321be41718cb0cf~mv2.jpg);
  --main-bg-img2: url(https://static.wixstatic.com/media/249619_3240fa44850a4abca8df17010bfebe07~mv2.jpg/v1/fill/w_1060,h_595,al_c,q_85,enc_auto/249619_3240fa44850a4abca8df17010bfebe07~mv2.jpg);
  --main-bg-img3: url(https://static.wixstatic.com/media/249619_2aaa8c716bef4fa89b2d88392db0f324~mv2.jpg/v1/fill/w_738,h_667,al_c,q_85,enc_auto/249619_2aaa8c716bef4fa89b2d88392db0f324~mv2.jpg);
  --main-bg-img4: url(https://static.wixstatic.com/media/249619_1e709ceaba6941958fe51efb7cb8ef6e~mv2.jpg/v1/fill/w_1060,h_536,al_b,q_85,enc_auto/249619_1e709ceaba6941958fe51efb7cb8ef6e~mv2.jpg);
  --logo-img: url(http://dev.novus.kr/soteria/img/soteria_logo.png);

  --swiper-image-ratio: 33.3%;
  --swiper-width: 20%;
  --swiper-inactive-scale: .85;
  --swiper-mobile-width: 90%;
  --swiper-mobile-inactive-scale: .95;
}

.st-header {
    height: 63px;
    background-color: #1d1d20;
    display: flex;
    justify-content: center;
}
.st-header-content {
    width: 100%;
    height: 100%;
    max-width: var(--page-max-width);
    display: flex;
    justify-content: space-between;
}
.st-logo {
    width: 12rem;
    background-image: var(--logo-img);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 80%;
}
.st-menu {
    display: flex;
    align-items: center;
    gap: 2rem;
    color: #fff;
    font-size: 0.95rem;
}
.st-menu-item {
    cursor: pointer;
}

.st-wrap * {
    box-sizing: border-box;
}

.st-tech-bg-video {
    width: 100%;
    height: auto;
    position: relative;
    z-index: 0;    
}

.st-tech-video-layer {
    position: relative;
    width: 100%;
    height: auto;
    display: flex;
    justify-content: center;
    align-items: center;

}

.st-tech-video-layer:after {
    content: '';
    box-shadow: inset 0px 20rem 20rem 0px #1d1d20;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    position: absolute;
}

.st-tech-video-layer .st-main-title {
    position: absolute;
    z-index: 10;
    text-shadow: 0 0 16px rgba(0,0,0,0.5);
}

.st-layer1 {
    height: calc(100vh - 63px);
    background-image: var(--main-bg-img);
    display: flex;
    justify-content: center;
    align-items: center;
}
.st-main-title {
    color: #fff;
    font-size: 4rem;
    text-align: center;
    line-height: 5rem;
}
.st-text-bold {
    font-weight: bold;
}
.st-text-bold-small {
    font-size: 2.5rem;
    font-weight: bold;
}
.st-text-slim {
    font-weight: 400;
}
.st-text-slim-small {
    font-size: 2.5rem;
    font-weight: 400;
}
.st-main-subtitle {
    font-size: 1.3rem;
    margin: 1rem 0rem;
    letter-spacing: 0.2rem;
}
.st-discover-icon {
    width: 18px;
    height: 18px;
}

.st-discover-icon g {
    fill: #1877F2;
}

.st-discover-now {
    font-size: 1.3rem;
    letter-spacing: 0.1rem;
    display: inline;
    cursor: pointer;
}

.st-wrap {
    overflow-y: auto;
}


.st-layer2 {
    padding: 8rem 20rem;
    display: flex;
    justify-content: center;
    line-height: 4rem;
}

.st-layer2 .st-main-section {
    width: 100%;
}

.st-layer2-section {
    max-width: var(--page-max-width);    
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
}
.st-main-text1 {
    font-size: 3rem;
    text-align: center;
    line-height: 1.2;
}

.st-main-fields {
    margin-top: 4.5rem;
}

.st-main-field {
    border-top: 1px #000 solid;
    margin-top: 4rem;
}
.st-field-title {
    font-size: 1.3rem;
    font-weight: 600;
    height: auto;
    display: inline;
}
.st-field-content {
    line-height: 1.5rem;
}


.st-layer3 {
    padding: 8rem 30rem;
    background-image: url(http://dev.novus.kr/soteria/img/aiblur.jpg);
    background-repeat: no-repeat;
    background-size: cover;
}
.st-layer3-section {
    max-width: var(--page-max-width);    
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
}
.st-main-text2 {
    text-align: center;
    color: #fff;
    font-size: 1.4rem;
    font-weight: 600;
}

.st-main-text3 {
    text-align: center;
    color: #fff;
    font-size: 3rem;
    font-weight: bold;
    margin: 2rem 0rem;
    line-height: 3.6rem;
}

.st-main-text4 {
    text-align: center;
    color: #fff;
    font-size: 1.2rem;
    width: 80%;
    line-height: 1.4;
    margin-bottom: 5rem;
}

.st-main-text5 {
    color: #fff;
    font-size: 3rem;
    font-weight: bold;
    margin: 2rem 0rem;
    line-height: 4rem;
    z-index: 10;
}

.st-main-text6 {
    text-align: left;
    color: #fff;
    font-size: 1.2rem;
    width: 50%;
    margin-bottom: 5rem;
    width: 100%;
    z-index: 10;
    line-height: 1.7rem;
}

.st-main-text7 {
    text-align: left;
    color: #fff;
    font-size: 1.4rem;
    font-weight: 600;
    z-index: 10;
}

.st-main-text8 {
    text-align: left;
    color: #fff;
    font-size: 1.2rem;
    width: 50%;
    margin-top: 1rem;
    width: 100%;
    line-height: 1.7rem;
}

.st-main-text9 {
    text-align: left;
    color: #cecece;
    font-size: 0.9rem;
    margin-top: 3rem;
    width: 100%;
}

.st-main-text10 {
    text-align: center;
    color: #141516;
    font-size: 2rem;
    margin: 1rem 0rem;
    width: 100%;
}

.st-main-text11 {
    text-align: center;
    color: #8d8d8d;
    font-size: 1rem;
    margin: 3rem 0rem;
    width: 100%;
    line-height: 1.5rem;
}

.st-main-text12 {
    text-align: center;
    color: #8d8d8d;
    font-size: 3rem;
    margin: 3rem 0rem;
    width: 100%;
    line-height: 4rem;
}

.st-main-text13 {
    text-align: center;
    color: #fff;
    font-size: 2rem;
    font-weight: bold;
    line-height: 4rem;
}

.st-main-text14 {
    text-align: center;
    color: #fff;
    font-size: 3rem;
    font-weight: bold;
    line-height: 4rem;
}

.st-main-text15 {
    text-align: left;
    color: #fff;
    font-size: 2.4rem;
    font-weight: 600;
    margin: 2rem 0rem;
    width: 100%;
}

.st-main-text16 {
    text-align: left;
    color: #fff;
    font-size: 0.9rem;
    line-height: 1.4rem;
}

.st-main-text17 {
    text-align: center;
    font-size: 3rem;
    font-weight: 600;
}

.st-main-text18 {
    text-align: center;
    font-size: 1rem;
    max-width: 50%;
    line-height: 1.4;
    margin: 2rem 0rem;
}

.st-center {
    text-align: center;
    z-index: 10;
}

.st-whr-button {
    border: 1px #fff solid;
    font-size: 1.1rem;
    color: #fff;
    padding: 0.5rem 5rem;
    border-radius: 2rem;
    cursor: pointer;
    background-color: rgba(0,0,0,0);
    transition: all 0.2s ease;
}
.st-whr-button:hover {
    background-color: #fff;
    color: #222;
}

.st-blk-button {
    border: 1px #222 solid;
    font-size: 1.1rem;
    color: #222;
    padding: 0.5rem 5rem;
    border-radius: 2rem;
    cursor: pointer;
    background-color: rgba(0,0,0,0);
    transition: all 0.2s ease;
}
.st-blk-button:hover {
    background-color: #222;
    color: #fff;
}


.st-layer4 {

}
.st-layer4-section {
    width: 100%;
    display: flex;
    justify-content: center;
}
.st-layer4-section .st-box-left {
    width: 50%;
    padding: 5rem 10rem;
    box-sizing: border-box;
    background-image: url(http://dev.novus.kr/soteria/img/foundry.jpg);
    background-repeat: no-repeat;
    background-size: cover;
    display: flex;
    flex-direction: column;
    position: relative;
    justify-content: space-between;
}
.st-layer4-section .st-box-left:before, .st-layer4-section .st-box-right:before {
    content: '';
    box-shadow: inset 0px 20rem 20rem 0px rgba(0,0,0,0.8);
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    position: absolute;
}
.st-layer4-section .st-box-right {
    width: 50%;
    padding: 5rem 10rem;
    box-sizing: border-box;
    background-image: url(http://dev.novus.kr/soteria/img/lowpower.jpg);
    background-repeat: no-repeat;
    background-size: cover;
    display: flex;
    position: relative;
    flex-direction: column;
    justify-content: space-between;
}
.st-main-img {
    width: 100%;
    text-align: center;
    padding: 3rem;
    box-sizing: border-box;
}

.st-main-img img {
    width: 50%;
}


.st-layer5 {
    padding: 8rem 30rem;
    background-image: url(http://dev.novus.kr/soteria/img/mission.jpg);
    background-repeat: no-repeat;
    background-size: cover;
    position: relative;
}
.st-layer5-section {
    max-width: var(--page-max-width);    
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
    z-index: 2;
    position: relative;
}
.st-layer5.eco-tech {
    background-image: url(http://dev.novus.kr/soteria/img/eco.jpg);
}
.st-opacity-back {
    position: absolute;
    top: 0rem;
    left: 0rem;
    width: 100%;
    height: 100%;
    background-color: rgba(0,0,0,0.5);
    z-index: 1;
}

.st-layer6 {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 3rem;
}
.st-layer6-section {
    background-color: #171717;
    padding: 5rem;
    max-width: var(--page-max-width); 
    width: 70%;
    border-radius: 1.5rem;
}
.st-switches-container * {
    box-sizing: border-box;
}
.st-switches-container {
    width: 16rem;
    position: relative;
    display: flex;
    padding: 0;
    position: relative;
    border: 1px #fff solid;
    line-height: 3rem;
    border-radius: 3rem;
    margin: 3rem 0rem;
    box-sizing: border-box;
}

.st-switches-container input {
    visibility: hidden;
    position: absolute;
    top: 0;
}

.st-switches-container label {
    width: 50%;
    padding: 0;
    margin: 0;
    text-align: center;
    cursor: pointer;
    color: white;
}

.st-switch-wrapper {
    position: absolute;
    top: 0;
    bottom: 0;
    width: 50%;
    padding: 0.15rem;
    z-index: 3;
    transition: transform .5s cubic-bezier(.77, 0, .175, 1);
    /* transition: transform 1s; */
}

.st-switch {
    border-radius: 3rem;
    background: white;
    height: 100%;
}

.st-switch div {
    width: 100%;
    text-align: center;
    opacity: 0;
    display: block;
    color: #171717;
    transition: opacity .2s cubic-bezier(.77, 0, .175, 1) .125s;
    will-change: opacity;
    position: absolute;
    top: 0;
    left: 0;
}

.st-switches-container input:nth-of-type(1):checked~.st-switch-wrapper {
    transform: translateX(0%);
}

.st-switches-container input:nth-of-type(2):checked~.st-switch-wrapper {
    transform: translateX(100%);
}

.st-switches-container input:nth-of-type(1):checked~.st-switch-wrapper .st-switch div:nth-of-type(1) {
    opacity: 1;
}

.st-switches-container input:nth-of-type(2):checked~.st-switch-wrapper .st-switch div:nth-of-type(2) {
    opacity: 1;
}

.st-progress-bar {
	width: 100%;
	height: 0.7rem;
	margin: 0 auto;
	position: relative;
	border-radius: 35px;
	overflow: hidden;
	background-color: rgba(0,0,0,0);
}

.st-progress-child {
	width: 100%;
	height: 100%;
}

.st-progress {
	color: white;
	text-align: center;
	animation-direction: reverse;
	background: #e5405e;
	background: linear-gradient(90deg, rgba(23,23,23,1) 0%, rgba(23,23,23,1) 6%, rgba(45,15,102,1) 60%, rgba(0,212,255,1) 100%);
}

.st-progress-field {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.st-shrinker {
	background-color: black;
	position: absolute;
	top: 0;
	right: 0;
	width: 100%;
}

.st-timelapse {
	animation-name: timelapse;
	animation-fill-mode: forwards;
	animation-duration: 2s;
	animation-timing-function: cubic-bezier(.86, .05, .4, .96);
}

.st-progress-title {
    color: #fff;
    font-size: 1.3rem;
    font-weight: 600;
}

.st-progress-container {
    display: flex;
    margin-top: 3rem;
}

.st-progress-disable .st-progress-text1,
.st-progress-disable .st-progress-text2, 
.st-progress-disable .st-progress-text3, 
.st-progress-disable .st-progress-text4 {
    color: #3a3a3a;
}

.st-progress-disable .st-progress {
    background: linear-gradient(90deg, rgba(23,23,23,1) 0%, rgba(23,23,23,1) 6%, rgba(58,58,58,1) 100%);;    
}

.st-progress-text1 {
    color: #fff;
    font-weight: 600;
    font-size: 3.4rem;
}
.st-progress-text2 {
    color: #fff;
    font-weight: 600;
    font-size: 2.4rem;
}
.st-progress-text3 {
    color: #fff;
    font-weight: 600;
    font-size: 1.3rem;
}
.st-progress-magnify {
    width: 8rem;
    position: relative;
    top: 0.4rem;
    text-align: center;
}
.st-progress-ms {
    width: 6rem;
    justify-content: flex-start;
    display: flex;
    align-items: flex-end;
}
.st-progress-1 {
    width: 30%;
}

.st-progress-2 {
    width: 45%;
}

.st-progress-3 {
    width: 60%;
}

.st-progress-4 {
    width: 75%;
}
@keyframes timelapse {
	0% {
		width: 100%;
	}
	100% {
		width: 0%;
	}
}


.st-layer7 {
    padding: 3rem;
}
.st-layer7-section {
    width: 100%;
    max-width: var(--page-max-width);
    display: flex;
    gap: 1rem;
    padding: 3rem 5rem;
}

.st-layer7-section .st-box-left {
    width: 50%;
    padding: 5rem;
    box-sizing: border-box;
    background-color: #ececec;
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
}

.st-layer7-section .st-box-right {
    width: 50%;
    padding: 5rem;
    box-sizing: border-box;
    background-color: #ececec;
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
}


.st-layer8 {
    padding: 10rem 3rem;
    background: linear-gradient(180deg,rgba(0,0,0,0) 0%,#000 73.44%);
    display: flex;
    justify-content: center;
    align-items: center;
}
.st-horiz-field {
    width: 100%;
    max-width: var(--sub-max-width);
    display: flex;
}

.st-horiz-left {
    width: 50%;
    padding: 3rem;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}
.st-horiz-right {
    width: 50%;
    padding: 3rem;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}

.st-horiz-right img,
.st-horiz-left img {
    width: 100%;
    border-radius: 1rem;
}


.st-layer9 {
    padding: 3rem;
}
.st-layer9-section {
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
}

.st-slider-container {
    margin-top:3rem;
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
}

.st-swiper {
    position: relative;
    width: 100%;
    height: calc(var(--swiper-width) * var(--swiper-image-ratio) / 100%);
    overflow: hidden;
}

.st-swiper-slide {
  position: relative;
  width: var(--swiper-width);
  opacity: .5;
  transform: scale(.84);
  transition: all .3s ease-in-out;
  overflow: hidden;
  border-radius: 0.4285rem;
}

.st-swiper-slide:hover {
    opacity: 1;
}

.swiper-backface-hidden .st-swiper-slide {
  transform: scale(.84) translateZ(0);
}

.st-swiper-slide.st-swiper-slide-active {  
  transform: scale(1) !important;
  opacity: 1 !important;
}

.swiper-backface-hidden .st-swiper-slide.st-swiper-slide-active {
  transform: scale(1) translateZ(0) !important;
}

.st-swiper-image {
  position: relative;
  width: 100%;
  padding-top: var(--swiper-image-ratio);
}

.st-swiper-image .st-image {
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
  background-color: #fff;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 0 12px rgba(0,0,0,0.15);
}

.st-swiper-image .st-image img {
    width: 30%;
    height: auto;
}

.st-swiper-button-next,
.st-swiper-button-prev {
  padding: 8px;
  width: 12px;
  height: 12px;
  margin-top: 0;
  background-color: rgba(0, 0, 0, .4);
  border-radius: 50%;
}

.st-swiper-button-next::after,
.st-swiper-button-prev::after {
  color: #fff;
  font-size: 12px;
}

.st-swiper-button-next {
  transform: translate(50%, -50%);
  right: calc((100% - var(--swiper-width)) / 2);
}

.st-swiper-button-prev {
  transform: translate(-50%, -50%);
  left: calc((100% - var(--swiper-width)) / 2);
}

.st-layer10 {
    padding: 3rem;
    display: flex;
    justify-content: center;
    background-color: #f4f4f4;
}
.st-layer10-section {
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
    max-width: var(--sub-max-width);
}

.st-layer10-section .st-field-item {
    width: 33.333%;
    display: flex;
    flex-direction: column;
}
.st-layer10-section .st-three-field {
    display: flex;
    gap:2rem;
    margin: 2rem 0rem;
}
.st-layer10-section .st-field-item img {
    width: 100%;
    border-radius: 1rem;
}

.st-layer10-section .st-field-title {
    margin: 1rem 0rem;
}

.st-layer10-section .st-field-text {
    color: #979797;
    line-height: 1.3;
}
.st-footer {
    width: 100%;
    padding-top: 3.5rem;
    padding-bottom: 2rem;
    background-color: #1d1d20;
    display: flex;
    justify-content: center;
}
.st-footer-wrap {
    width: 50%;
    max-width: var(--page-max-width);
    padding: 0 15%;
    margin-bottom: 2rem;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}
.st-footer-logo-menu {
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 10rem;
}
.st-footer-logo {
    width: 70%;
}
.st-footer-logo img {
    width: 11rem;
}
.st-footer-content {
    width: 200%;
    display: flex;
    justify-content: flex-end;
    align-items: center;
    gap: 5rem;
}
.footer-menu-title {
    font-size: 1rem;
    color: #fff;
    letter-spacing: -0.03rem;
    cursor: pointer;
}
.st-footer-desc {
    width: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    margin-top: 0.7rem;
    font-size: 1rem;
    color: #fff;
    letter-spacing: -0.03rem;
    margin-bottom: 5rem;
    line-height: 1.2rem;
}
.st-footer-email {
    width: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    margin-top: 1.2rem;
    font-size: 0.8rem;
    color: #fff;
    letter-spacing: -0.03rem;
}
.footer-seperator {
    width: 100%;
    height: 1px;
    background-color: #ddd;
    margin: 3rem 0;
}
.st-footer-address {
    width: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    margin-top: 1rem;
    font-size: 0.8rem;
    color: #fff;
