    box-sizing: border-box;
    background-color: #ececec;
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
}


.st-layer8 {
    padding: 10rem 3rem;
    background: linear-gradient(180deg,rgba(0,0,0,0) 0%,#000 73.44%);
    display: flex;
    justify-content: center;
    align-items: center;
}
.st-horiz-field {
    width: 100%;
    max-width: var(--sub-max-width);
    display: flex;
}

.st-horiz-left {
    width: 50%;
    padding: 3rem;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}
.st-horiz-right {
    width: 50%;
