    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}

.st-horiz-right img,
.st-horiz-left img {
    width: 100%;
    border-radius: 1rem;
}


.st-layer9 {
    padding: 3rem;
}
.st-layer9-section {
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
}

.st-slider-container {
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
}

.st-swiper {
    position: relative;
    width: 100%;
    height: calc(var(--swiper-width) * var(--swiper-image-ratio) / 100%);
    overflow: hidden;
}

.st-swiper-slide {
  position: relative;
  width: var(--swiper-width);
  opacity: .5;
  transform: scale(.84);
  transition: all .3s ease-in-out;
  overflow: hidden;
  border-radius: 0.4285rem;
}

.swiper-backface-hidden .st-swiper-slide {
  transform: scale(.84) translateZ(0);
}
        
.st-swiper-slide.st-swiper-slide-active {  
  transform: scale(1) !important;
  opacity: 1 !important;
}

.swiper-backface-hidden .st-swiper-slide.st-swiper-slide-active {
  transform: scale(1) translateZ(0) !important;
}

.st-swiper-image {
  position: relative;
  width: 100%;
  padding-top: var(--swiper-image-ratio);
}
    
.st-swiper-image .st-image {
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
  background-color: #929ec9;
}

.st-swiper-button-next,
.st-swiper-button-prev {
  padding: 8px;
  width: 12px;
  height: 12px;
  margin-top: 0;
  background-color: rgba(0, 0, 0, .4);
  border-radius: 50%;
}

.st-swiper-button-next::after,
.st-swiper-button-prev::after {
  color: #fff;
  font-size: 12px;
}

.st-swiper-button-next {
  transform: translate(50%, -50%);
  right: calc((100% - var(--swiper-width)) / 2);
}

.st-swiper-button-prev {
  transform: translate(-50%, -50%);
  left: calc((100% - var(--swiper-width)) / 2);
}

.st-layer10 {
