    align-items: center;
    margin-top: 0.7rem;
    font-size: 1rem;
    color: #fff;
    letter-spacing: -0.03rem;
    margin-bottom: 5rem;
    line-height: 1.2rem;
}
.st-footer-email {
    width: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    margin-top: 1.2rem;
    font-size: 0.8rem;
    color: #fff;
    letter-spacing: -0.03rem;
}
.footer-seperator {
    width: 100%;
