  --main-bg-img2: url(https://static.wixstatic.com/media/249619_3240fa44850a4abca8df17010bfebe07~mv2.jpg/v1/fill/w_1060,h_595,al_c,q_85,enc_auto/249619_3240fa44850a4abca8df17010bfebe07~mv2.jpg);
  --main-bg-img3: url(https://static.wixstatic.com/media/249619_2aaa8c716bef4fa89b2d88392db0f324~mv2.jpg/v1/fill/w_738,h_667,al_c,q_85,enc_auto/249619_2aaa8c716bef4fa89b2d88392db0f324~mv2.jpg);
  --main-bg-img4: url(https://static.wixstatic.com/media/249619_1e709ceaba6941958fe51efb7cb8ef6e~mv2.jpg/v1/fill/w_1060,h_536,al_b,q_85,enc_auto/249619_1e709ceaba6941958fe51efb7cb8ef6e~mv2.jpg);
  --logo-img: url(http://dev.novus.kr/soteria/img/soteria_logo.png);
}
.st-header {
    height: 63px;
    background-color: #1d1d20;
    display: flex;
    justify-content: center;
}
.st-header-content {
    width: 100%;
    height: 100%;
    max-width: var(--page-max-width);
    display: flex;
    justify-content: space-between;
}
.st-logo {
    width: 12rem;
    background-image: var(--logo-img);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 80%;
}
.st-menu {
    display: flex;
    align-items: center;
    gap: 2rem;
    color: #fff;
    font-size: 0.95rem;
}
.st-menu-item {
    cursor: pointer;
}
.st-wrap {
    overflow-y: auto;
}
.st-wrap * {
    box-sizing: border-box;
}
.contact-top-wrap {
    height: 20rem;
    background-image: url(http://dev.novus.kr/soteria/img/mbg.jpg);
    box-sizing: border-box;
    padding: 1rem 15% 3rem;
    display: flex;
    align-items: end;
    background-position: center;
    background-size: cover;
}
.contact-top-title {
    font-size: 3rem;
    font-weight: 600;
    letter-spacing: -0.03rem;
    color: #fff;
}
.contact-body-wrap {
    width: 100%;
    height: calc(100%- 20rem);
    padding: 5rem 15%;
}
.contact-map-wrap {
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}
.contact-map-box {
    width: 100%;
}
.contact-map-wrap:not(:first-child) .contact-map-box {
    padding-top: 5rem;
}
.contact-map-box img {
    width: 100%;
    height: 100%;
}
.contact-map-text {
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin: 3rem 0;
}
.map-contact-info {
    width: 100%;
    display: flex;
    justify-content: flex-end;
    align-items: center;
    gap: 3rem;
}
.map-contact-label {
    width: 350%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
}
.map-label-title {
    margin-right: 9rem;
    font-size: 2.5rem;
    font-weight: 600;
    letter-spacing: -0.03rem;
}
.map-desc {
    font-size: 1rem;
    letter-spacing: -0.03rem;
    color: #666;
}
.contact-info-wrap {
    display: flex;
    justify-content: center;
    align-items: center;
}
.conatct-email-icon {
    width: 1.2rem;
    height: 1.2rem;
}
.conatct-call-icon {
    width: 1.2rem;
    height: 1.2rem;
}
.conatct-call-icon path {
    fill: #1877F2;
}
.contact-info-wrap span {
    font-size: 1rem;
    letter-spacing: -0.03rem;
    color: #222;
    margin-left: 0.5rem;
}
.contact-seperator {
    width: 100%;
    height: 1px;
    background-color: #d1d1d1;
}
.contact-form-wrap {
    padding-top: 5rem;
    width: 100%;
    display: flex;
}
.form-label-wrap {
    width: 80%;
}
.form-input-wrap {
    width: 300%;
    display: flex;
    flex-direction: column;
}
.form-label-text {
    padding-top: 2rem;
    font-size: 2.5rem;
    font-weight: 700;
    position: relative;
}
.form-label-text:after {
    position: absolute;
    left: 30px;
    transform: translateX(-50%);
    top: 0;
    content: '';
    width: 55px;
    height: 5px;
    background-color: #0065b3;
}
.form-row {
    width: 100%;
    display: flex;
    gap: 1.5rem;
    margin-bottom: 1rem;
}
.form-input {
    width: 50%;
    padding: 1rem 1.5rem;
    border: 1px solid #e9e9e9;
    background-color: #fff;
    color: #a5a5a5;
}
.form-input:hover, .form-input:focus,
.form-textarea:hover, .form-textarea:focus{
    border-color: #0065b3 !important;
    box-shadow: none;
    color: #333 !important;
}
.form-textarea {
    font-family: inherit;
    width: 100%;
    height: 15rem;
    padding: 1rem 1.5rem;
    border: 1px solid #e9e9e9;
    background-color: #fff;
    color: #a5a5a5;
}
.form-checkbox-wrap {
    width: 100%;
    display: flex;
    margin-top: 2rem;
}
.form-label {
    display: flex;
    justify-content: center;
    align-items: center;
}
.form-checkbox {
    margin-right: 1rem;
    width: 1rem;
    height: 1rem;
}
.form-submit-wrap {
    display: flex;
    justify-content: flex-end;
    align-items: center;
    margin-top: 7rem;
}
.form-submit-btn {
    background-color: #0065b3;
    width: 10rem;
    height: 3rem;
    padding: 1rem;
    color: #fff;
    letter-spacing: 0.15rem;
    font-size: 1rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
}
.form-img-wrap {
    width: 300%;
    display: flex;
    flex-direction: column;
}
.form-submit-icon {
    width: 1.2rem;
    height: 1.2rem;
}
.st-footer {
    width: 100%;
    padding-top: 3.5rem;
    padding-bottom: 2rem;
    background-color: #1d1d20;
    display: flex;
    justify-content: center;
}
.st-footer-wrap {
    width: 50%;
    max-width: var(--page-max-width);
    padding: 0 15%;
    margin-bottom: 2rem;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}
.st-footer-logo-menu {
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 10rem;
}
.st-footer-logo {
    width: 70%;
    cursor: pointer;
}
.st-footer-logo img {
    width: 11rem;
}
.st-footer-content {
    width: 200%;
    display: flex;
    justify-content: flex-end;
    align-items: center;
    gap: 5rem;
}
.footer-menu-title {
    font-size: 1rem;
    color: #fff;
    letter-spacing: -0.03rem;
    cursor: pointer;
}
.st-footer-desc {
    width: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    margin-top: 0.7rem;
    font-size: 1rem;
    color: #fff;
    letter-spacing: -0.03rem;
    margin-bottom: 5rem;
    line-height: 1.2rem;
}
.st-footer-email {
    width: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    margin-top: 1.2rem;
    font-size: 0.8rem;
    color: #fff;
    letter-spacing: -0.03rem;
}
.footer-seperator {
    width: 100%;
    height: 1px;
    background-color: #ddd;
    margin: 3rem 0;
}
.st-footer-address {
    width: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    margin-top: 1rem;
    font-size: 0.8rem;
    color: #fff;
    letter-spacing: -0.03rem;
}
.st-footer-row {
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.st-footer-copyright {
    width: 100%;
    display: flex;
    justify-content: flex-end;
    align-items: center;
    margin-top: 1rem;
    font-size: 0.8rem;
    color: #fff;
    letter-spacing: -0.03rem;
}

