    text-decoration: line-through;
}

[order-list-modal] .price-label,
[order-list-modal] .price-value {
    line-height: 1.4;
    font-family: inherit;
    font-size: 1.2rem;
    font-weight: 700;
}

[order-list-modal] .second-value, 
[order-list-modal] .second-label,
[order-list-modal] .home-text 
{
    font-size: 1.1rem;
}

[order-list-modal] .home-text {
    margin-left:0.3rem;
}

[order-list-modal] .time-label-text {
    font-size: 1rem;
    letter-spacing: -0.025rem;
    color: #1e59f1;
}
[order-list-modal] .time-label-text.employee-call {
    color: #f0873a;
}

menu.css:
[main-menu-page] .menu-wrap {
    width: 100%;
    height: 100%;
    position: relative;
    display: flex;
    background-color: var(--theme-bg-color);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
}

[main-menu-page] .menu-left {
    width: calc(100% - 16rem);
    height: 100%;
}

[main-menu-page] .menu-right {
    position: relative;
    width: 16rem;
    height: 100%;
    box-shadow: -2px 0 2px rgb(0 0 0 / 10%);
}

[main-menu-page] .left-tabs {
    position: relative;
    background-color: rgb(255 255 255 / 90%);
    overflow: auto;
}

[main-menu-page] .left-tabs:after {
    content: "";
    position: absolute;
    top: 0;
    right: 0;
    width: 10rem;
    height: 100%;
    background-image: linear-gradient(90deg, transparent, #ffffff);
    opacity: 0.95;
}

[main-menu-page] .left-tabs::-webkit-scrollbar {
    width: 0;
    height: 0;
}

[main-menu-page] .header-wrap {
    display: flex;
    justify-content: space-between;
    height: 4rem;
    padding:0 1.5rem;
    background-color: rgb(255 255 255 / 90%);
}

[main-menu-page] .product-info-basket {
    position: absolute;
    width: 4rem;
    height: 4rem;
    right: 1rem;
    bottom: 7rem;
    border-radius: 0.7rem;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #a773f7;
    color: #fff;   
}

[main-menu-page] .product-info-basket > span {
    font-size: 1.8rem;
}

[main-menu-page] .product-info {
    display: flex;
    height: 6.3rem;
}

[main-menu-page] .product-info-content {
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

[main-menu-page] .logo-part {
    display: flex;
    align-items: center;
}

[main-menu-page] .logo-part-img {
    width: 6.5rem;
    height: 2rem;
    background-repeat: no-repeat;
    background-size: contain;
    background-position: center;
}

[main-menu-page] .logo-part-title {
    color: var(--theme-color);
    font-size: 1.1rem;
    font-weight: 500;
    letter-spacing:-0.03rem;
}

[main-menu-page] .logo-part-seperator {
    border-right: 1px solid var(--theme-color);
    margin:0 1rem;
    height: 1rem;
}

[main-menu-page] .table-part {
    display: flex;
    align-items: flex-start;
}

[main-menu-page] .table-box {
    display: flex;
    height: 3.8rem;
    flex-direction: column;
    padding:0.6rem 1rem;
    background: rgb(255 255 255 / 90%);
    border-bottom-left-radius: 0.5rem;
    border-bottom-right-radius: 0.5rem;
    box-shadow: 0 0 8px rgb(0 0 0 / 15%);
}

[main-menu-page] .table-box.reselect {
    margin-right: 1rem;
    height: 3.8rem;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
}

[main-menu-page] .table-box.reselect span {
    margin-right:0.3rem;
    color: #3C3A3A;
}

[main-menu-page] .dark-mode .table-box {
    background-color: rgb(249 96 87 / 90%);
}

[main-menu-page] .table-label {
    text-align: center;
    color: var(--theme-color);
    margin-bottom:0.2rem;
    font-size: 0.8rem;
}

[main-menu-page] .reselect .table-label {
    font-size: 1.2rem;
    margin-bottom: 0px !important;
    letter-spacing: -0.03rem;
    font-weight: 500;
}

[main-menu-page] .table-value {
    text-align: center;
    font-size: 1.6rem;
    font-weight: 600;
    line-height: 1;
    letter-spacing: -0.03rem;
    color: var(--theme-color);
}

[main-menu-page] .main-header-link {
    display: flex;
    padding:0 2rem;
    height:100%;
    align-items: center;
}

[main-menu-page] .main-header-link.is-active {
    border-bottom: 3px solid var(--theme-color);
}


[main-menu-page] .link-text {
    font-size: 1.1rem;
    font-weight: 700;
    color: var(--theme-color);
    white-space: nowrap;
}

[main-menu-page] .list-wrap {
    width: 100%;
    height: calc(100% - 8rem);
}

[main-menu-page] .app-card-img {
    width:100%;
    height:calc(100% - 6rem);
    background-size:cover;
    background-position: center;
}

[main-menu-page] .product-name {
    display: flex;
    align-items: center;
    justify-content: center;
}

[main-menu-page] .product-price {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

[main-menu-page] .origin-price {
    font-size: 0.9rem;
    color: #888;
    text-decoration: line-through;
}

[main-menu-page] .price-box {
    display: flex;
    align-items: center;
}

[main-menu-page] .discount-rate {
    letter-spacing: -0.025rem;
    font-weight: 500;
    font-size: 1rem;
    color: #c51406;
    margin-right: 0.3rem;
}

[main-menu-page] .price-value {
    font-size: 1.1rem;
    letter-spacing: -0.025rem;
    font-weight: 600;
}

[main-menu-page] .name-text {
    color: var(--theme-color);
    font-size: 1.1rem;
    margin: 0.6rem 0 0.2rem 0;
    font-weight: 600;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
}

[main-menu-page] .subname-text {
    color: var(--theme-color);
    font-size: 0.8rem;
    margin: 0.6rem 0 0.2rem 0.2rem;
    font-weight: 600;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    opacity: 0.7;
}

[main-menu-page] .price-text {
    color: var(--theme-color);
    margin: 0 0 1rem 0;
    font-size: 1.2rem;
    font-weight: 600;
    letter-spacing: -0.04rem;
}

[main-menu-page] .menu-right-btns {
    display: flex;
    height: 4rem;
}

[main-menu-page] .menu-right-btn {
    box-sizing: border-box;
    height: 4rem;
    border-top: 1px solid #fff;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(58 109 236 / 90%);
}

[main-menu-page] .call-btn {
    width: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(58 109 236 / 90%);
    transition: all ease 0.3s;
}

[main-menu-page] .call-btn:hover,
[main-menu-page] .menu-right-btn:hover {
    background-color: #1e59f1;
}

[main-menu-page] .call-btn:first-child {
    border-right: 1px solid #fff;
    margin-right:0;
}

[main-menu-page] .call-btn span, 
[main-menu-page] .menu-right-btn span {
    color: #fff;
    font-size: 1.2rem;
    letter-spacing: -0.01rem;
    font-weight: 500;
    cursor: pointer;
}

[main-menu-page] .menu-right-basket {
    height:4rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(255 255 255 / 90%);
}

[main-menu-page] .dark-mode .menu-right-basket {
    background-color: var(--theme-bg-color);
}

[main-menu-page] .menu-right-basket span {
    color: var(--theme-color);
    font-size: 1.2rem;
    font-weight:600;
}

[main-menu-page] .menu-order-btn {
    height: 4rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(249 96 87 / 90%);
}

[main-menu-page] .menu-order-btn span {
    color: #fff;
    font-size: 1.3rem;
    font-weight: 600;
}

[main-menu-page] .menu-items {
    position: relative;
    width: 100%;
    height: calc(100% - 16rem);
    background-color: var(--theme-bg-middle-color);
    overflow: auto;
}

[main-menu-page] .menu-right:after {
    content: "";
    position: absolute;
    bottom: 4rem;
    width: 100%;
    height: 4rem;
    background-image: linear-gradient(180deg, transparent, #ffffff);
    opacity: 0.85;
}

[main-menu-page] .menu-item {
    padding: 1rem;
    background-color: var(--scrollbar-bg);
    margin-bottom: 0.5rem;
}

[main-menu-page] .menu-item:last-child {
    margin-bottom: 0;
}

[main-menu-page] .item-info {
    display: flex;
    align-items: center;
    justify-content: flex-start;
}

[main-menu-page] .menu-item-img {
    background-size:cover;
    background-position: center;
    min-width:6vh;
    width:6vh;
    height:6vh;
    border-radius: 0.5rem;
    margin-right: 0.5rem;
}

[main-menu-page] .item-detail {
    width: calc(100% - 6vh);
    display: flex;
    flex-direction: column;
}

[main-menu-page] .item-product-name.basket {
    color: var(--theme-color);
    font-size:1.1rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
    margin-bottom: 0;
    white-space: nowrap;
}

[main-menu-page] .item-product-price {
    margin-top: 0.4rem;
    font-size:1.1rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
    color: var(--theme-color);
}

[main-menu-page] .item-product-adding {
    display: flex;
    flex-direction: column;
}

[main-menu-page] .item-info-remove {
    display: flex;
    background-color: #fff;
    border-radius: 0.4rem;
    margin-left:0.8rem;
    width: 3.0rem;
    min-width: 3.0rem;
    height: 2.8rem;
    justify-content: center;
    align-items: center;
}

[main-menu-page] .dark-mode .item-info-remove svg {
    fill:var(--theme-color);    
}

[main-menu-page] .quantity-box {
    margin-top: 1rem;
    position: relative;
    display: flex;
    border-radius: 0;
    overflow:hidden;
    background-color: transparent;
    border: 0;
}

[main-menu-page] .dark-mode .qbox-input {
    background-color: var(--theme-color);
}

[main-menu-page] .qbox-input {
    box-sizing: border-box;
    width:100%;
    margin:0;
    padding:0.3rem 0.5rem;
    border:0;
    font-size: 1.2rem;
    font-weight: 600;
    background-color: var(--content-bg-hover);
    text-align: center;
}

[main-menu-page] .qbox-btn {
    cursor: pointer;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #ffffff;
    width:3rem;
    min-width: 3rem;
    height:2.8rem;
    user-select: none;
}

[main-menu-page] .qbox-btn.minus {
    border-top-left-radius: 0.4rem;
    border-bottom-left-radius: 0.4rem;
}

[main-menu-page] .qbox-btn.minus svg {
    width:1.5rem;
}

[main-menu-page] .qbox-btn.plus {
    border-top-right-radius: 0.4rem;
    border-bottom-right-radius: 0.4rem;
}

[main-menu-page] .qbox-btn.plus svg {
    width:1.5rem;
}

[main-menu-page] .dark-mode .qbox-btn {
    background-color: var(--theme-color);
}

[main-menu-page] .tag-wrap {
    display: flex;
    padding: 0 1rem;
    align-items: center;
    justify-content: center;
    height: 2rem;
    margin-bottom:0.3rem;
}
[main-menu-page] .case-tag {
    padding: 0.3rem;
    background-color: #c51406;
    color: #fff;
    font-size: 0.85rem;
    border-radius: 0.3rem;
    margin-right: 0.3rem;
}
[main-menu-page] .case-tag:last-child {
    margin-right: 0;
}
[main-menu-page] .item-product-options {
    color: #444;
    font-size: 1rem;
    font-weight: 500;
    margin-top: 0.2rem;
}
[main-menu-page] .item-product-side {
    color: #777;
    font-size: 0.9rem;
    font-weight: 500;
    margin-top: 0.2rem;
}
[main-menu-page] .call-btn-icon {
    margin-right: 0.1rem;
    display: flex;
    justify-content: center;
    align-items: center;
}

[main-menu-page] .icon-color {
    fill: var(--theme-color);
}

employeecall.css:
[employee-call-modal] .pop-up.employee-call {
    width: 90% !important;
    height: 90%;
}

[employee-call-modal] .employee-call-header {
    display: flex;
    align-items: center;
}

[employee-call-modal] .employee-call-text {
    font-family: inherit;
    font-size: 1.5rem;
    font-weight: 700;
    border-right: 1px solid #c5c3c3;
    padding-right: 1.2rem;
    line-height: 1.4;
}

[employee-call-modal] .employee-call-table {
    height: 100%;
    padding-left: 1.2rem;
    display: flex;
    align-items: center;
}

[employee-call-modal] .table-text, 
[employee-call-modal] .table-value {
    font-size: 1.4rem;
    line-height: 1.4;
    font-weight: 600;
    color: #1e59f1;
}

[employee-call-modal] .employee-call-table .table-value {
    padding-left: 0.2rem;
}

[employee-call-modal] .pop-up__title.employee-call {
    border-bottom: none;
} 

[employee-call-modal] .time-close {
    display: flex;
    justify-content: center;
    align-items: center;
    padding-right: 2rem;
}

[employee-call-modal] .time-close .time-second {
    display: flex;
    font-family: inherit;
    font-weight: 500;
    color: #1e59f1;
    line-height: 1.4;
}

[employee-call-modal] .employee-call-close {
    display: flex;
}

[employee-call-modal] .employee-call-wrapper {
    width: 100%;
    height: calc(100% - 3.3rem);
    display: flex;
    border-top: 1px solid #e8e8e8;
}

[employee-call-modal] .employee-call-left {
    width: calc(100% - 300px);
    height: 100%;
}

[employee-call-modal] .employee-call-right {
    width: 300px;
    height: 100%;
    background-color: #f2f2f2;
    border-left: 1px solid #e8e8e8;
    border-right: 1px solid #e8e8e8;
}

[employee-call-modal] .call-item {
    width: calc(25% -  1.125rem);
    height: 5.5rem;
    padding: 0.5rem;
    border-radius: 1rem;
    border: 2px solid #e8e8e8;
    color: #8a8a8a;
    cursor: pointer;
    transition: 0.3s;
    white-space: nowrap;
    font-weight: 600;
    line-height: 1.4;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 1.1rem;
}

[employee-call-modal] .call-item.active {
    border: 2px solid #1e59f1;
    color: #1e59f1;
}

[employee-call-modal] .call-item-list {
    gap: 1.5rem;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    flex-wrap: wrap;
    overflow-y: scroll;
    padding: 1rem 1rem 1rem 0px;
}

[employee-call-modal] .call-item-detail {
    width: 100%;
    height: calc(100% - 5rem);
    display: flex;
    flex-direction: column;
    overflow: auto;
}

[employee-call-modal] .call-item-button {
    width: 100%;
    height: 5rem;
    color: #fff;
    background-color: #1e59f1;
    position: static;
    bottom: 0px;
    left: 0px;
    display: flex;
    border-radius: 0.5rem;
    justify-content: center;
    align-items: center;
    line-height: 1.4;
    font-size: 1.25rem;
}

[employee-call-modal] .detail-item {
    width: 100%;
    height: 10rem;
    background-color: #fff;
    margin-bottom: 0.7rem;
    padding: 1rem;
    box-shadow: 0 0 5px rgb(0 0 0 / 15%);
}

[employee-call-modal] .detail-item .item-text {
    width: 100%;
    height: 3rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
}


[employee-call-modal] .item-text-label {
    line-height: 1.4;
    font-size: 1.25rem;
    font-weight: 700;
}

[employee-call-modal] .item-quantity {
    width: 100%;
    height: 4rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    border: 2px solid #e8e8e8;
    border-radius: 0.7rem;
}

[employee-call-modal] .item-quantity-minus,
[employee-call-modal] .item-quantity-plus {
    width: 30%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
}

[employee-call-modal] .item-quantity-value {
    width: 40%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 1.5rem;
    font-weight: 500;
    line-height: 1.4;
    border: 0;
    text-align: center;
}

[employee-call-modal] .second-value, 
[employee-call-modal] .second-label,
[employee-call-modal] .home-text 
{
    font-size: 1.1rem;
}

[employee-call-modal] .home-text {
    margin-left: 0.3rem;
}

[employee-call-modal] .close-svg-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    padding:0.3rem;
    cursor: pointer;
}

[employee-call-modal] .item-close-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    padding:0.3rem;
}

menudetail.css:
[main-menu-detail] .pop-up.menu-detail {
    width: 90% !important;
    height: 90%;
}

[main-menu-detail] .menu-detail-wrapper {
    width: 100%;
    height: 100%;
    display: flex;
}

[main-menu-detail] .pop-up__title.menu-detail {
    position: absolute;
    border-bottom: none;
    padding-bottom: 0;
    margin-bottom: 0;
    right: 2rem;
    top: 2rem;
}

[main-menu-detail] .menu-detail-left {
    width: 50%;
    height: 100%;
    padding: 0 1.2rem 1.2rem 0rem;
    border-right: 1px solid #e8e8e8;
}

[main-menu-detail] .menu-detail-right {
    width: 50%;
    height: 100%;
}

[main-menu-detail] .menu-detail-img {
    width: 100%;
    height: auto;
    max-height: 70%;
    border-radius: 1rem;
    margin-bottom: 1.5rem;
}

[main-menu-detail] .menu-detail-name {
    font-size: 1.5rem;
    font-weight: 700;
    line-height: 1.4;
    margin-bottom: 0.5rem;
    letter-spacing: -0.025rem;
}

[main-menu-detail] .menu-detail-price {
    display: flex;
    align-items: center;
    margin-bottom: 1.5rem;
}

[main-menu-detail] .menu-detail-price .price-value,
[main-menu-detail] .menu-detail-price .price-label {
    font-size: 1.3rem;
    font-weight: 500;
    line-height: 1.4;
    letter-spacing: -0.025rem;
}

[main-menu-detail] .menu-detail-desc {
    word-break: keep-all;
    font-size: 1.1rem;
    font-weight: 400;
    line-height: 1.4;
    color: #7c7c7c;
    letter-spacing: -0.025rem;
}

[main-menu-detail] .basket-order-btn {
    width: calc(100% - 1.2rem);
    height: 4rem;
    background-color: #1e59f1;
    padding: 1rem;
    margin: 1.2rem 0 0 1.2rem;
    border-radius: 1rem;
    display: flex;
    justify-content: center;
    align-items: center;
}

[main-menu-detail] .menu-price-value,
[main-menu-detail] .menu-price-label {
    font-size: 1.2rem;
    font-weight: 600;
    color: #fff;
} 

[main-menu-detail] .main-menu-options {
    width: 100%;
    padding: 1.2rem 0 0 1.2rem;
    margin-bottom:1rem;
}

[main-menu-detail] .side-menu-options {
    width: 100%;
    padding: 1.2rem 0 0 1.2rem;
}

[main-menu-detail] .menu-option-text {
    width: 100%;
    display: flex;
    margin-bottom: 0.6rem;
}

[main-menu-detail] .main-side-text {
    font-size: 1.3rem;
    font-weight: 700;
    line-height: 1.4;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: 0.2rem;
}

[main-menu-detail] .main-side-label {
    font-size: 1.1rem;
    font-weight: 600;
    line-height: 1.4;
    display: flex;
    justify-content: center;
    align-items: center;
    color: #ce584b;
}

[main-menu-detail] .menu-option-choice {
    width: 100%;
    height: 3rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 0.5rem;
    border: 2px solid #e5e5e5;
    border-radius: 0.5rem;
    padding: 0.5rem;
}

[main-menu-detail] .menu-option-choice.empty {
    justify-content: center;
}

[main-menu-detail] .menu-option-choice.empty .option-price {
    display: none;
}

[main-menu-detail] .menu-option-choice.empty .checkbox + label:before {
    display: none;
}

[main-menu-detail] .menu-option-choice:last-child {
    margin-bottom: 0;
}

[main-menu-detail] .menu-option-choice.selected {
    border: 2px solid #1e59f1;
    border-radius: 0.5rem;
