admin.css:
.menu-wrap {
    width: 100%;
    height: 100%;
    position: relative;
    display: flex;
    background-color: var(--theme-bg-color);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
}

.menu-only {
    width: 100%;
    height: 100%;
}

.header-wrap {
    display: flex;
    justify-content: space-between;
    height: 4rem;
    padding:0 1.5rem;
}

.logo-part {
    display: flex;
    align-items: center;
}

.logo-part-img {
    width: 6.5rem;
    height: 2rem;
    background-repeat: no-repeat;
    background-size: contain;
    background-position: center;
}

.logo-part-title {
    color: var(--theme-color);
    font-size: 1.1rem;
    font-weight: 500;
    letter-spacing:-0.03rem;
}

.logo-part-seperator {
    border-right: 1px solid var(--theme-color);
    margin:0 1rem;
    height: 1rem;
}

.table-part {
    display: flex;
    align-items: flex-start;
}

.table-box {
    display: flex;
    flex-direction: column;
    padding:0.6rem 1rem;
    background: rgb(255 255 255 / 90%);
    border-bottom-left-radius: 0.5rem;
    border-bottom-right-radius: 0.5rem;
}

.dark-mode .table-box {
    background-color: rgb(249 96 87 / 90%);
}

.table-label {
    text-align: center;
    color: var(--theme-color);
    margin-bottom:0.2rem;
    font-size: 0.8rem;
}

.table-value {
    text-align: center;
    font-size: 1.6rem;
    font-weight: 600;
    letter-spacing: -0.03rem;
    color: var(--theme-color);
}

.main-header-link {
    display: flex;
    padding:0 2rem;
    height:100%;
    align-items: center;
}

.main-header-link.is-active {
    border-bottom: 2px;
}


.link-text {
    font-size: 1.1rem;
    font-weight: 700;
    color: var(--theme-color);
}

.list-wrap {
    width: 100%;
    height: calc(100% - 8rem);
}

.app-card-img {
    width:100%;
    height:calc(100% - 6.3rem);
    background-size:cover;
    background-position: center;
}

.product-name {
    display: flex;
    align-items: center;
    justify-content: center;
}

.product-price {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

.origin-price {
    font-size: 0.9rem;
    color: #888;
    text-decoration: line-through;
}

.price-box {
    display: flex;
    align-items: center;
}

.discount-rate {
    letter-spacing: -0.025rem;
    font-weight: 500;
    color: #c51406;
    margin-right: 0.3rem;
}

.price-value {
    font-size: 1.1rem;
    letter-spacing: -0.025rem;
    font-weight: 600;
}

.name-text {
    color: var(--theme-color);
    font-size: 1.1rem;
    margin: 0.6rem 0 0.2rem 0;
    font-weight: 600;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
}

.price-text {
    color: var(--theme-color);
    margin: 0 0 1rem 0;
    font-size: 1.2rem;
    font-weight: 600;
    letter-spacing: -0.04rem;
}

.menu-right-btns {
    display: flex;
    height: 4rem;
}


.call-btn {
    width: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(58 109 236 / 90%);
    transition: all ease 0.3s;
}

.call-btn:hover {
    background-color: #1e59f1;
}

.call-btn:first-child {
    margin-right: 1px;
}

.call-btn span {
    color: #fff;
    font-size: 1.1rem;
    font-weight: 600;
}

.menu-right-basket {
    height:4rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(255 255 255 / 90%);
}

.dark-mode .menu-right-basket {
    background-color: var(--theme-bg-color);
}

.menu-right-basket span {
    color: var(--theme-color);
    font-size: 1.1rem;
    font-weight:600;
}

.menu-order-btn {
    height: 4rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(249 96 87 / 90%);
}

.menu-order-btn span {
    color: #fff;
    font-size: 1.3rem;
    font-weight: 600;
}

.menu-items {
    width: 100%;
    height: calc(100% - 12rem);
    background-color: var(--theme-bg-middle-color);
    overflow: auto;
}

.menu-item {
    padding: 1rem;
    background-color: var(--scrollbar-bg);
    margin-bottom: 1rem;
}

.menu-item:last-child {
    margin-bottom: 0;
}

.item-info {
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.menu-item-img {
    background-size:cover;
    background-position: center;
    min-width:10vh;
    width:10vh;
    height:10vh;
    border-radius: 0.5rem;
    margin-right: 1rem;
}

.item-detail {
    width: calc(100% - 10vh - 24px);
    display: flex;
    flex-direction: column;
}

.item-product-name {
    color: var(--theme-color);
    font-size:1.2rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
    margin-bottom: 0.4rem;
}

.item-product-price {
    font-size:1.1rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
    color: var(--theme-color);
}

.dark-mode .item-info-remove svg {
    fill:var(--theme-color);    
}

.quantity-box {
    margin-top: 1rem;
    position: relative;
    display: flex;
    border-radius: 0.5rem;
    overflow:hidden;
    background-color: var(--content-bg-hover);
    border: 1px solid var(--border-color);
}

.dark-mode .qbox-input {
    background-color: var(--theme-color);
}

.qbox-input {
    box-sizing: border-box;
    width:100%;
    margin:0;
    padding:0.3rem 0.5rem;
    border:0;
    font-size: 1.3rem;
    font-weight: 600;
    background-color: var(--content-bg-hover);
    text-align: center;
}

.qbox-btn {
    cursor: pointer;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: var(--content-bg);
    width:20%;
    min-width: 25%;
    height:2.8rem;
}

.dark-mode .qbox-btn {
    background-color: var(--theme-color);
}

.tag-wrap {
    display: flex;
    padding: 0 1rem;
    align-items: center;
    justify-content: center;
    height: 2rem;
}

.case-tag {
    padding: 0.3rem;
    background-color: #c51406;
    color: #fff;
    font-size: 0.85rem;
    border-radius: 0.3rem;
}

.category-tabs, .func-tabs {
    display: flex;
    align-items: center;
    margin-bottom:1rem;
}

.light-mode .tab-item {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0.6rem 1rem;
    background: #fff;
    border: solid 1px rgb(197, 197, 198);
    border-radius: 0.2rem;
    margin-right: 0.5rem;
    cursor: pointer;
}

.tab-item-text {
    font-size:1rem;
    color: var(--theme-color);
    letter-spacing: -0.025rem;
    font-weight: 500;
}

.admin-table {
    width: 100%;
    height: 100%;
}

