[order-manager] .price-text {
    color: var(--theme-color);
    margin: 0 0 1rem 0;
    font-size: 1.2rem;
    font-weight: 600;
    letter-spacing: -0.04rem;
}

[order-manager] .menu-right-btns {
    display: flex;
    height: 4rem;
}


[order-manager] .call-btn {
    width: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(58 109 236 / 90%);
    transition: all ease 0.3s;
}

[order-manager] .call-btn:hover {
    background-color: #1e59f1;
}

[order-manager] .call-btn:first-child {
    margin-right: 1px;
}

[order-manager] .call-btn span {
    color: #fff;
    font-size: 1.1rem;
    font-weight: 600;
}

[order-manager] .menu-right-basket {
    height:4rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(255 255 255 / 90%);
}

[order-manager] .dark-mode .menu-right-basket {
    background-color: var(--theme-bg-color);
}

[order-manager] .menu-right-basket span {
    color: var(--theme-color);
    font-size: 1.1rem;
    font-weight:600;
}

[order-manager] .menu-order-btn {
    height: 4rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(249 96 87 / 90%);
}

[order-manager] .menu-order-btn span {
    color: #fff;
    font-size: 1.3rem;
    font-weight: 600;
}

[order-manager] .menu-items {
    width: 100%;
    height: calc(100% - 12rem);
    background-color: var(--theme-bg-middle-color);
    overflow: auto;
}

[order-manager] .menu-item {
    padding: 1rem;
    background-color: var(--scrollbar-bg);
    margin-bottom: 1rem;
}

[order-manager] .menu-item:last-child {
    margin-bottom: 0;
}

[order-manager] .item-info {
    display: flex;
    align-items: center;
    justify-content: space-between;
}

[order-manager] .menu-item-img {
    background-size:cover;
    background-position: center;
    min-width:10vh;
    width:10vh;
    height:10vh;
    border-radius: 0.5rem;
    margin-right: 1rem;
}

[order-manager] .item-detail {
    width: calc(100% - 10vh - 24px);
    display: flex;
    flex-direction: column;
}

[order-manager] .item-product-name {
    color: var(--theme-color);
    font-size:1.2rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
    margin-bottom: 0.4rem;
}

[order-manager] .item-product-price {
    font-size:1.1rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
    color: var(--theme-color);
}

[order-manager] .dark-mode .item-info-remove svg {
    fill:var(--theme-color);    
}

[order-manager] .quantity-box {
    margin-top: 1rem;
    position: relative;
    display: flex;
    border-radius: 0.5rem;
    overflow:hidden;
    background-color: var(--content-bg-hover);
    border: 1px solid var(--border-color);
}

[order-manager] .dark-mode .qbox-input {
    background-color: var(--theme-color);
}

[order-manager] .qbox-input {
    box-sizing: border-box;
    width:100%;
    margin:0;
    padding:0.3rem 0.5rem;
    border:0;
    font-size: 1.3rem;
    font-weight: 600;
    background-color: var(--content-bg-hover);
    text-align: center;
}

[order-manager] .qbox-btn {
    cursor: pointer;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: var(--content-bg);
    width:20%;
    min-width: 25%;
    height:2.8rem;
}

[order-manager] .dark-mode .qbox-btn {
    background-color: var(--theme-color);
}

[order-manager] .tag-wrap {
    display: flex;
    padding: 0 1rem;
    align-items: center;
    justify-content: center;
    height: 2rem;
}

[order-manager] .case-tag {
    padding: 0.3rem;
    background-color: #c51406;
    color: #fff;
    font-size: 0.85rem;
    border-radius: 0.3rem;
}

[order-manager] .category-tabs,
[order-manager] .func-tabs {
    display: flex;
    align-items: center;
    margin-bottom:1rem;
}

[order-manager] .light-mode .tab-item {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0.6rem 1rem;
    background: #fff;
    border: solid 1px rgb(197, 197, 198);
    border-radius: 0.2rem;
    margin-right: 0.5rem;
}

[order-manager] .tab-item-text {
    font-size:1rem;
    color: var(--theme-color);
    letter-spacing: -0.025rem;
    font-weight: 500;
}

[order-manager] .admin-table {
    width: 100%;
    height: 100%;
}

[order-manager] .table-name-block {
    width: 100%;
    height: 4rem;
    min-height: 4rem;
    display: flex;
    justify-content: space-between;
    padding: 0 1.5rem;
    align-items: center;
    margin-bottom: 0.8rem;
}

[order-manager] .table-name-tag {
    width: 90%;
    height: 100%;
    border-bottom-left-radius: 0.5rem;
    border-bottom-right-radius: 0.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #c51406;
}

[order-manager] .table-name-text {
    font-size: 1.6rem;
    color: #222;
    font-weight: 600;
    letter-spacing: -0.025rem;
}
[order-manager] .orders-sum {
    width: 100%;
    height: 3rem;
    min-height: 3rem;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: var(--theme-admin-bg-color);
}
[order-manager] .orders-sum-row {
    display: flex;
    align-items: center;
}
[order-manager] .orders-sum-label {
    margin-right: 1.5rem;
    letter-spacing: -0.025rem;
    font-size: 1rem;
    font-weight: 500;
}
[order-manager] .order-sum-value {
    font-size: 1.2rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
}
[order-manager] .orders-ul {
    width: 100%;
    height: 100%;
    padding: 0 1rem;
    overflow: auto;
}
[order-manager] .orders-li {
    display: flex;
    width: 100%;
    padding: 0.2rem 0;
    justify-content: space-between;
}
[order-manager] .orders-li-name {
    font-size: 1.1rem;
    letter-spacing: -0.03rem;
    font-weight: 500;
}
[order-manager] .orders-li-count {
    font-size: 1.1rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
}
[order-manager] .table-btns {
    display: flex;
    width: 100%;
    height: 6rem;
}
[order-manager] .table-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    background-color: #222;
    color: #fff;
    border-right: 1px solid #fff;
}
[order-manager] .table-btn:last-child {
    border-right: 0;
}
[order-manager] .table-btn span {
    font-size: 1.1rem;
}
[order-manager] .orderview-wrap {
    width: 100%;
    height: 100%;
    display: flex;
}
[order-manager] .orderview-left {
    display: flex;
    flex-direction: column;
    width:100%;
}
[order-manager] .orderview-right {
    width: 90px;
    height: 100%;
}
[order-manager] .orderview-title {
    width: 100%;
    height: 5rem;
    display: flex;
    align-items: center;
    padding-left: 1rem;
}
[order-manager] .orderview-title-text {
    font-size: 1.6rem;
    font-weight: 700;
    margin-right: 1rem;
    letter-spacing: -0.025rem;
}
[order-manager] .orderview-time {
    width: 100%;
    height: 3rem;
    display: flex;
    align-items: center;
    padding: 0 1rem;
}

[order-manager] .orderview-time span {
    font-size: 1.1rem;
    letter-spacing: -0.025rem;
}

[order-manager] .orderview-time svg {
    width: 16px !important;
    height: 16px !important;
    margin-right: 0.5rem !important;
}

[order-manager] .orderview-btns {
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

[order-manager] .orderview-btn {
    width: 100%;
    height: 100%;
    background-color: rgb(28 38 63);
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    border-bottom: 1px solid #515e85;
}

[order-manager] .orderview-btn span {
    color: #fff;
    font-size: 1rem;
}
[order-manager] .orderview-table-index {
    background-color: #c51406;
    border-radius: 0.3rem;
    padding: 0.3rem 0.5rem;
}
[order-manager] .orderview-table-name {
    color: #fff;
    font-size: 1.2rem;
    letter-spacing: -0.025rem;
}
[order-manager] .table-status {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0.6rem 1rem;
    background: #9e9e9e;
    border-radius: 2rem;
}
[order-manager] .table-status-msg {
    font-size: 1.2rem;
    color: #fff;
}
[order-manager] .table-status-sign {
    width: 0.8rem;
    height: 0.8rem;
    border-radius: 1rem;
    background-color: #fff;
    margin-right: 0.3rem;
}
[order-manager] .table-status.active {
    background: #3ec644;
}
[order-manager] .orderview-icon svg {
    margin-right: 0 !important;
    margin-bottom: 0.3rem;
}
[order-manager] .orders-manager {
    width: 100%;
    height: 100%;
    display: flex;
}

[order-manager] .tab-wrapper {
    min-width: 6rem;
    flex-direction: column;
    display: flex;
}
[order-manager] .content-wrapper {
    width: 100%;
}
[order-manager] .orders-manager .tab-box {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    height: 33.33333%;
    background-color: rgb(28 38 63);
    border-bottom: 1px solid #515e85;
}
[order-manager] .orders-manager .tab-box.selected {
    background-color: #1e59f1;
}
[order-manager] .tab-box-label {
    font-size: 1.4rem;
    font-weight: 700;
    color: #fff;
}
[order-manager] .tab-box-value {
    font-size: 1.5rem;
    font-weight: 600;
    color: #fff;
    margin-top: 0.5rem;
}
[orders-detail-modal] .orders-detail .orders-detail-item.deleted .order-detail, 
[orders-detail-modal] .orders-detail .orders-detail-item.deleted .order-price {
    color: #c51406;
    text-decoration: line-through;
}
[orders-detail-modal] .orders-detail .orders-detail-item.deleted  .qbox-btn {
    background: #eeeeee;
    cursor: not-allowed;
}
[orders-detail-modal] .orders-detail .orders-detail-item.deleted .qbox-input {
    background: #eeeeee;
    color: #d0d0d0;
    cursor: not-allowed;
}
[orders-detail-modal] .orders-detail .orders-detail-item.deleted .qbox-btn svg path {
    fill: #d0d0d0;
}

ordersdetail.css:
[orders-detail-modal] .pop-up.orders-detail {
    width: 90% !important;
    height: 90%;
}

[orders-detail-modal] .order-number {
    width: 5%;
    min-width: 5rem;
    max-width: 15rem;
    height: 4rem;
    color: #1e59f1;
    line-height: 1.4;
    font-size: 1.2rem;
    display: flex;
    justify-content: center;
    align-items: center;
}

[orders-detail-modal] .order-time {
    width: 20%;
    min-width: 10rem;
    max-width: 20rem;
    height: 4rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: flex-start;
}

[orders-detail-modal] .time-label {
    display:flex;
    align-items: center;
    margin-bottom: 0.2rem;
}

[orders-detail-modal] .time-value {
    line-height: 1.4;
    font-size: 1rem;
    display:flex;
    align-items: center;
}

[orders-detail-modal] .order-detail {
    width: 55%;
    display: block;
}

[orders-detail-modal] .item-name-quantity {
    display: flex;
    align-items: center;
}

[orders-detail-modal] .order-price {
    text-align: right;
    min-width: 10rem;
    max-width: 10rem;
    height: 4rem;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: 1rem;
}

[orders-detail-modal] .order-quantity {
    width: 20%;
    min-width: 180px;
    max-width: 15rem;
    height: 4rem;
    display: flex;
    justify-content: center;
    align-items: center;
}

[orders-detail-modal] .price-label, 
[orders-detail-modal] .price-value {
    line-height: 1.4;
    font-family: inherit;
    font-size: 1.2rem;
    font-weight: 700;
}

[orders-detail-modal] .time-label-text {
    font-size: 1rem;
    letter-spacing: -0.025rem;
    color: #1e59f1;
}

[orders-detail-modal] .type-label-text {
    font-size: 1rem;
    letter-spacing: -0.025rem;
    color: #1e59f1;
}

[orders-detail-modal] .latest .time-label-text {
    color: #1e59f1;
}

[orders-detail-modal] .orders-detail .orders-detail-item {
    border-bottom: 1px solid #e3e3e3;
}

[orders-detail-modal] .orders-detail .content-button-wrapper {
    margin: 0;
    min-width: 6rem;
    max-width: 6rem;
    height: 4rem;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-left: 1rem;
}

[orders-detail-modal] .orders-detail .content-button {
    height: 2.8rem;
    background-color: #f07d3a;
    margin: 0;
    border-radius: 0.5rem;
}

[orders-detail-modal] .orders-detail .content-button:not(.open):hover {
  background: #f07d3a;
}

[orders-detail-modal] .orders-detail .order-detail {
    width: 100%;
}

[orders-detail-modal] .orders-detail .quantity-box {
    margin-top: 0px;
    border: 1px solid #e3e3e3;
    box-sizing: border-box;
    width: 100%;
}

[orders-detail-modal] .orders-detail .qbox-input {
    border-right: 1px solid #e3e3e3;
    border-left: 1px solid #e3e3e3;
}

popuser.css:
[pop-user-modal] .pop-up.pop-user {
    width: 90% !important;
    height: auto;
}

[pop-user-modal] .pop-user-header {
    display: flex;
    align-items: center;
}

[pop-user-modal] .pop-user-text {
    font-family: inherit;
    font-size: 1.5rem;
    font-weight: 700;
    margin-right: 0.5rem;
    line-height: 1.4;
}

[pop-user-modal] .cpop-user-desc {
    height: 100%;
    margin-left: 0.5rem;
    display: flex;
    align-items: center;
}

[pop-user-modal] .pop-user-desc .user-text {
    font-size: 1.1rem;
    line-height: 1.4;
    font-weight: 600;
    color: #8a8a8a;
}

[pop-user-modal] .user-setting-item {
    width: 100%;
    height: 5rem;
    display: flex;
    align-items: center;
    margin-bottom: 1rem;
}

[pop-user-modal] .user-setting-item:first-child {
    height: 2rem;
}

[pop-user-modal] .setting-label {
    width: 12rem;
    height: auto;
    margin-right: 5rem;
    font-size: 1.1rem;
    font-weight: 600;
    line-height: 1.4;
    color:#222;
}

[pop-user-modal] .setting-input {
    width: 40%;
    height: 3rem;
    border-radius: 0.5rem;
    border: 2px solid #8a8a8a;
    padding: 0.5rem;
    font-size: 1.1rem;
}

[pop-user-modal] .setting-input:read-only {
    color: #a8a8a8;
    border: 1px solid #a8a8a8;
}

[pop-user-modal] .setting-input.required,
[pop-user-modal] .setting-input:hover {
    border: 2px solid #1e59f1;
}

[pop-user-modal] .setting-img img {
    border-radius:0.5rem;
    width: 10rem;
    height: 10rem;
}

[pop-user-modal] .required-label {
    font-size: 0.95rem;
    color: #1e59f1;
    display: flex;
    align-items: center;
}

[pop-user-modal] .user-setting-item button {
    margin-right: 0.5rem;
}

[pop-user-modal] .content-button-wrapper .modify {
    background-color: #f0873a;
    margin-right: 0.5rem;
}
[pop-user-modal] .content-button-wrapper .content-button.modify:hover {
    background-color: #f0873a;
}

login.css:
.login-wrap {
    width: 100vw;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #fff;
} 
.login-box {
    width: 50%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}
.login-logo {
    margin-bottom: 2rem;
}
.logo-img {
    width: 10rem;
    height: 10rem;
}
.login-label {
    margin-bottom: 1.5rem;
}
.label-text {
    font-size: 2.2rem;
    font-weight: 500;
    letter-spacing: -0.02rem;
    color: #222;
}
.login-desc {
    margin-bottom: 3rem;
}
.desc-text {
    font-size: 1.2rem;
    color: #999;
    letter-spacing: -0.03rem;
}
.login-input {
    width: 30rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-content: center;
    margin-bottom: 5rem;
    box-sizing: border-box;
}
.login-input-icon {
    color: #444 !important;
    font-size: 2rem;
    margin-left: 0.4rem;
}
.login-input-icon.hide {
    visibility: hidden;
}
.hide-pw .id-wrap {
    border-radius: 0.5rem;
}
.id-wrap {
    border: 1px solid #d2d2d7;
    height: 4rem;
    border-top-left-radius: 0.5rem;
    border-top-right-radius: 0.5rem;
    overflow: hidden;
}
.id-wrap.focus {
    box-sizing: border-box;
    outline-style: solid;
    outline-width: 5px;
    outline-color: #0071e3;
    z-index: 1;
}
.focus .login-input-label {
    font-style: normal;
    font-weight: normal;
    font-size: 1.3rem;
    line-height: 1.6rem;
    letter-spacing: -0.03rem;
    color: rgb(128, 128, 128);
    cursor: text;
    margin-left: 0.8rem;
    position: absolute;
    transform-origin: left top;
    transition: all 0.1s ease-in-out 0s;
    transform: translate(0px, 0.5rem) scale(0.68);
}
.is-value .login-input-label {
    font-style: normal;
    font-weight: normal;
    font-size: 1.3rem;
    line-height: 1.6rem;
    letter-spacing: -0.03rem;
    color: rgb(128, 128, 128);
    cursor: text;
    margin-left: 0.8rem;
    position: absolute;
    transform-origin: left top;
    transition: all 0.1s ease-in-out 0s;
    transform: translate(0px, 0.5rem) scale(0.68);
}
.hide-pw .pw-wrap {
    visibility: hidden;
}
.pw-wrap.focus {
    box-sizing: border-box;
    outline-style: solid;
    outline-width: 5px;
    outline-color: #0071e3;
    z-index: 1;
}
.pw-wrap {
    border: 1px solid #d2d2d7;
    height: 4rem;
    border-top: none;
    border-bottom-left-radius: 0.5rem;
    border-bottom-right-radius: 0.5rem;
}
.login-input-label {
    font-style: normal;
    font-weight: normal;
    font-size: 1.3rem;
    line-height: 1.6rem;
    letter-spacing: -0.03rem;
    color: rgb(128, 128, 128);
    cursor: text;
    margin-left: 0.8rem;
    position: absolute;
    transform-origin: left top;
    transition: all 0.1s ease-in-out 0s;
    transform: translate(0px, 1.3rem) scale(1);
}
.login-input-box {
    width: 88%;
    min-width: 17.5rem;
    border: none;
    font-size: 1rem;
    margin: 2rem 0 0 0.5rem;
    padding: 0;
    padding-left: 0.5rem;
    outline: 0;
    font-style: normal;
    font-weight: normal;
    line-height: 1.6rem;
    background: rgb(255, 255, 255);
    color: rgb(26, 26, 26);
}
.id-save-wrap {
    display: flex;
    justify-content: center;
    align-items: center;
    margin-bottom: 2.2rem;
}
.id-save-checkbox {
    margin-right: 0.5rem;
}
.id-save-label {
    font-size: 1rem;
    font-weight: 500;
    color: #777;
    letter-spacing: -0.03rem;
}
.login-forget {
    display: flex;
    justify-content: center;
    align-items: center;
}
.forget-text {
    font-size: 1.1rem;
    color: #1e59f1;
    letter-spacing: -0.03rem;
}
.forget-icon {
    color: #1e59f1 !important;
    font-size: 1.1rem;
    margin-left: 0.3rem;
}
