css:
[order-manager] .menu-wrap {
    width: 100%;
    height: 100%;
    position: relative;
    display: flex;
    background-color: var(--theme-bg-color);
    backdrop-filter: blur(5px);
    -webkit-backdrop-filter: blur(5px);
}

[order-manager] .menu-only {
    width: 100%;
    height: 100%;
}

[order-manager] .header-wrap {
    display: flex;
    justify-content: space-between;
    background-color: #fff;
    height: 4rem;
    padding:0 1.5rem;
}

[order-manager] .left-tabs {
    background-color: #fff;
}

[order-manager] .logo-part {
    display: flex;
    align-items: center;
}

[order-manager] .logo-part-img {
    width: 6.5rem;
    height: 2rem;
    background-repeat: no-repeat;
    background-size: contain;
    background-position: center;
}

[order-manager] .logo-part-title {
    color: var(--theme-color);
    font-size: 1.1rem;
    font-weight: 500;
    letter-spacing:-0.03rem;
}

[order-manager] .logo-part-seperator {
    border-right: 1px solid var(--theme-color);
    margin:0 1rem;
    height: 1rem;
}

[order-manager] .table-part {
    display: flex;
    align-items: flex-start;
}

[order-manager] .table-box {
    display: flex;
    flex-direction: column;
    padding:0.6rem 1rem;
    background: rgb(255 255 255 / 90%);
    border-bottom-left-radius: 0.5rem;
    border-bottom-right-radius: 0.5rem;
}

[order-manager] .dark-mode .table-box {
    background-color: rgb(249 96 87 / 90%);
}

[order-manager] .table-label {
    text-align: center;
    color: var(--theme-color);
    margin-bottom:0.2rem;
    font-size: 0.8rem;
}

[order-manager] .table-value {
    text-align: center;
    font-size: 1.6rem;
    font-weight: 600;
    letter-spacing: -0.03rem;
    color: var(--theme-color);
}

[order-manager] .main-header-link {
    display: flex;
    padding:0 2rem;
    height:100%;
    align-items: center;
}

[order-manager] .main-header-link.is-active {
    border-bottom: 3px solid var(--theme-color);
}


[order-manager] .link-text {
    font-size: 1.1rem;
    font-weight: 700;
    color: var(--theme-color);
}

[order-manager] .list-wrap {
    width: 100%;
    height: calc(100% - 8rem);
}

[order-manager] .app-card-img {
    width:100%;
    height:calc(100% - 6.3rem);
    background-size:cover;
    background-position: center;
}

[order-manager] .product-name {
    display: flex;
    align-items: center;
    justify-content: center;
}

[order-manager] .product-price {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

[order-manager] .origin-price {
    font-size: 0.9rem;
    color: #888;
    text-decoration: line-through;
}

[order-manager] .price-box {
    display: flex;
    align-items: center;
}

[order-manager] .discount-rate {
    letter-spacing: -0.025rem;
    font-weight: 500;
    color: #c51406;
    margin-right: 0.3rem;
}

[order-manager] .price-value {
    font-size: 1.1rem;
    letter-spacing: -0.025rem;
    font-weight: 600;
}

[order-manager] .name-text {
    color: var(--theme-color);
    font-size: 1.1rem;
    margin: 0.6rem 0 0.2rem 0;
    font-weight: 600;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
}

[order-manager] .price-text {
    color: var(--theme-color);
    margin: 0 0 1rem 0;
    font-size: 1.2rem;
    font-weight: 600;
    letter-spacing: -0.04rem;
}

[order-manager] .menu-right-btns {
    display: flex;
    height: 4rem;
}


[order-manager] .call-btn {
    width: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(58 109 236 / 90%);
    transition: all ease 0.3s;
}

[order-manager] .call-btn:hover {
    background-color: #1e59f1;
}

[order-manager] .call-btn:first-child {
    margin-right: 1px;
}

[order-manager] .call-btn span {
    color: #fff;
    font-size: 1.1rem;
    font-weight: 600;
}

[order-manager] .menu-right-basket {
    height:4rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(255 255 255 / 90%);
}

[order-manager] .dark-mode .menu-right-basket {
    background-color: var(--theme-bg-color);
}

[order-manager] .menu-right-basket span {
    color: var(--theme-color);
    font-size: 1.1rem;
    font-weight:600;
}

[order-manager] .menu-order-btn {
    height: 4rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(249 96 87 / 90%);
}

[order-manager] .menu-order-btn span {
    color: #fff;
    font-size: 1.3rem;
    font-weight: 600;
}

[order-manager] .menu-items {
    width: 100%;
    height: calc(100% - 12rem);
    background-color: var(--theme-bg-middle-color);
    overflow: auto;
}

[order-manager] .menu-item {
    padding: 1rem;
    background-color: var(--scrollbar-bg);
    margin-bottom: 1rem;
}

[order-manager] .menu-item:last-child {
    margin-bottom: 0;
}

[order-manager] .item-info {
    display: flex;
    align-items: center;
    justify-content: space-between;
}

[order-manager] .menu-item-img {
    background-size:cover;
    background-position: center;
    min-width:10vh;
    width:10vh;
    height:10vh;
    border-radius: 0.5rem;
    margin-right: 1rem;
}

[order-manager] .item-detail {
    width: calc(100% - 10vh - 24px);
    display: flex;
    flex-direction: column;
}

[order-manager] .item-product-name {
    color: var(--theme-color);
    font-size:1.2rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
    margin-bottom: 0.4rem;
}

[order-manager] .item-product-price {
    font-size:1.1rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
    color: var(--theme-color);
}

[order-manager] .dark-mode .item-info-remove svg {
    fill:var(--theme-color);    
}

[order-manager] .quantity-box {
    margin-top: 1rem;
    position: relative;
    display: flex;
    border-radius: 0.5rem;
    overflow:hidden;
    background-color: var(--content-bg-hover);
    border: 1px solid var(--border-color);
}

[order-manager] .dark-mode .qbox-input {
    background-color: var(--theme-color);
}

[order-manager] .qbox-input {
    box-sizing: border-box;
    width:100%;
    margin:0;
    padding:0.3rem 0.5rem;
    border:0;
    font-size: 1.3rem;
    font-weight: 600;
    background-color: var(--content-bg-hover);
    text-align: center;
}

[order-manager] .qbox-btn {
    cursor: pointer;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: var(--content-bg);
    width:20%;
    min-width: 25%;
    height:2.8rem;
}

[order-manager] .dark-mode .qbox-btn {
    background-color: var(--theme-color);
}

[order-manager] .tag-wrap {
    display: flex;
    padding: 0 1rem;
    align-items: center;
    justify-content: center;
    height: 2rem;
}

[order-manager] .case-tag {
    padding: 0.3rem;
    background-color: #c51406;
    color: #fff;
    font-size: 0.85rem;
    border-radius: 0.3rem;
}

[order-manager] .category-tabs,
[order-manager] .func-tabs {
    display: flex;
    align-items: center;
    margin-bottom:1rem;
}

[order-manager] .light-mode .tab-item {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0.6rem 1rem;
    background: #fff;
    border: solid 1px rgb(197, 197, 198);
    border-radius: 0.2rem;
    margin-right: 0.5rem;
}

[order-manager] .tab-item-text {
    font-size:1rem;
    color: var(--theme-color);
    letter-spacing: -0.025rem;
    font-weight: 500;
}

[order-manager] .admin-table {
    width: 100%;
    height: 100%;
}

[order-manager] .table-name-block {
    width: 100%;
    height: 4rem;
    min-height: 4rem;
    display: flex;
    justify-content: space-between;
    padding: 0 1.5rem;
    align-items: center;
    margin-bottom: 0.8rem;
}

[order-manager] .table-name-tag {
    width: 90%;
    height: 100%;
    border-bottom-left-radius: 0.5rem;
    border-bottom-right-radius: 0.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #c51406;
}

[order-manager] .table-name-text {
    font-size: 1.6rem;
    color: #222;
    font-weight: 600;
    letter-spacing: -0.025rem;
}
[order-manager] .orders-sum {
    width: 100%;
    height: 3rem;
    min-height: 3rem;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: var(--theme-admin-bg-color);
}
[order-manager] .orders-sum-row {
    display: flex;
    align-items: center;
}
[order-manager] .orders-sum-label {
    margin-right: 1.5rem;
    letter-spacing: -0.025rem;
    font-size: 1rem;
    font-weight: 500;
}
[order-manager] .order-sum-value {
    font-size: 1.2rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
}
[order-manager] .orders-ul {
    width: 100%;
    height: 100%;
    padding: 0 1rem;
    overflow: auto;
}
[order-manager] .orders-li {
    display: flex;
    width: 100%;
    padding: 0.2rem 0;
    justify-content: space-between;
}
[order-manager] .orders-li-name {
    font-size: 1.1rem;
    letter-spacing: -0.03rem;
    font-weight: 500;
}
[order-manager] .orders-li-count {
    font-size: 1.1rem;
    letter-spacing: -0.03rem;
    font-weight: 600;
}
[order-manager] .table-btns {
    display: flex;
    width: 100%;
    height: 6rem;
}
[order-manager] .table-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    background-color: #222;
    color: #fff;
    border-right: 1px solid #fff;
}
[order-manager] .table-btn:last-child {
    border-right: 0;
}
[order-manager] .table-btn span {
    font-size: 1.1rem;
}
[order-manager] .orderview-wrap {
    width: 100%;
    height: 100%;
    display: flex;
}
[order-manager] .orderview-left {
    display: flex;
    flex-direction: column;
    width:100%;
}
[order-manager] .orderview-right {
    width: 90px;
    height: 100%;
}
[order-manager] .orderview-title {
    width: 100%;
    height: 5rem;
    display: flex;
    align-items: center;
    padding-left: 1rem;
}
[order-manager] .orderview-title-text {
    font-size: 1.6rem;
    font-weight: 700;
    margin-right: 1rem;
    letter-spacing: -0.025rem;
}
[order-manager] .orderview-time {
    width: 100%;
    height: 3rem;
    display: flex;
    align-items: center;
    padding: 0 1rem;
}

[order-manager] .orderview-time span {
    font-size: 1.1rem;
    letter-spacing: -0.025rem;
}

[order-manager] .orderview-time svg {
    width: 16px !important;
    height: 16px !important;
    margin-right: 0.5rem !important;
}

[order-manager] .orderview-btns {
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

[order-manager] .orderview-btn {
    width: 100%;
    height: 100%;
    background-color: rgb(28 38 63);
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    border-bottom: 1px solid #515e85;
}

[order-manager] .orderview-btn span {
    color: #fff;
    font-size: 1rem;
}
[order-manager] .orderview-table-index {
    background-color: #c51406;
    border-radius: 0.3rem;
    padding: 0.3rem 0.5rem;
}
[order-manager] .orderview-table-name {
    color: #fff;
    font-size: 1.2rem;
    letter-spacing: -0.025rem;
}
[order-manager] .table-status {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0.6rem 1rem;
    background: #9e9e9e;
    border-radius: 2rem;
}
[order-manager] .table-status-msg {
    font-size: 1.2rem;
    color: #fff;
}
[order-manager] .table-status-sign {
    width: 0.8rem;
    height: 0.8rem;
    border-radius: 1rem;
    background-color: #fff;
    margin-right: 0.3rem;
}
[order-manager] .table-status.active {
    background: #3ec644;
}
[order-manager] .orderview-icon svg {
    margin-right: 0 !important;
    margin-bottom: 0.3rem;
}
[order-manager] .orders-manager {
    width: 100%;
    height: 100%;
    display: flex;
}

[order-manager] .tab-wrapper {
    min-width: 120px;
    flex-direction: column;
    display: flex;
}
[order-manager] .content-wrapper {
    width: 100%;
}
[order-manager] .orders-manager .tab-box {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    height: 33.33333%;
    background-color: rgb(28 38 63);
    border-bottom: 1px solid #515e85;
}
[order-manager] .orders-manager .tab-box.selected {
    background-color: #1e59f1;
}
[order-manager] .tab-box-label {
    font-size: 1.6rem;
    font-weight: 700;
    color: #fff;
}
[order-manager] .tab-box-value {
    font-size: 1.8rem;
    font-weight: 600;
    color: #fff;
    margin-top: 0.5rem;
}
[orders-detail-modal] .orders-detail .orders-detail-item.deleted .order-detail, 
[orders-detail-modal] .orders-detail .orders-detail-item.deleted .order-price {
    color: #c51406;
    text-decoration: line-through;
}
[orders-detail-modal] .orders-detail .orders-detail-item.deleted  .qbox-btn {
    background: #eeeeee;
    cursor: not-allowed;
}
[orders-detail-modal] .orders-detail .orders-detail-item.deleted .qbox-input {
    background: #eeeeee;
    color: #d0d0d0;
    cursor: not-allowed;
}
[orders-detail-modal] .orders-detail .orders-detail-item.deleted .qbox-btn svg path {
    fill: #d0d0d0;
}
